class DummyAttribute(object):

	def __init__(self, value = 0):
		self.value = value

class DummyAttributes(object):

	def __getitem__(self, key):
		if key == "hierarchy": return DummyAttribute()
		if key == "dynamic": return DummyAttribute(1)
		#else: raise AttributeError, "!!!"
		else: return ""

class DummyObject(object):

	def __init__(self, vdomclass, name, action_name, context, index):
		self.__original=vdomclass
		self.name=name
		self.id=vdomclass.id
		self.type=vdomclass.type
		try: self.order=vdomclass.attributes["order"].value
		except: self.order=0
		self.dynamic={(action_name, context): vdomclass.type.dynamic}
		self.attributes=vdomclass.attributes#DummyAttributes()
		self.actions=vdomclass.actions
		self.types={self.type.id: self.type}
		self.containers={}
		self.libraries={}

	def get_attributes(self):
		return self.attributes

	def set_attribute_ex(self, name, value, x = 0):
		if self.attributes[name]:
			old_value = self.attributes[name].value
		else:
			old_value = value
		self.attributes[name].value = value
		return { "value": value, "old_value": old_value }

	def get_objects_list(self):
		return self.__original.get_objects_list()


class VDOM_connector(VDOM_object):

	tmp_list = []

	def create_dupes_recurse(self, application, res, object, children):
		result = res
		for index, xobject in enumerate(children):

			d_name = "vdomclass_%d" % index

			obj_new = DummyObject(xobject, d_name, e2vdom.virtual_context, object, 0)
			obj_new_id = obj_new.id

			self.tmp_list.append(obj_new_id)

			try:
				obj_new.set_name(xobject.name)
			except:
				pass

			obj_attr = xobject.get_attributes()
			for a in obj_attr:
				obj_new.set_attribute_ex(obj_attr[a].name, obj_attr[a].value, False)

			if xobject.type.container == 2:
				result = self.create_dupes_recurse(application, result, obj_new, xobject.get_objects_list())

		return result


	def do_compile(self, object, obj_data):
		result = ""

		if self.vdomclassid:
			self.tmp_list = []

			classname = u"conn" if self.classname == "" else self.classname

			source_object = application.objects.search(self.vdomclassid)

			if not source_object:
				result += u"Invalid vdomclass"
				return result

			if 'get_attributes' in dir(source_object):
				source_object_attr = source_object.get_attributes()
			else:
				debug('Object has no attributes: %s' % source_object)
				source_object_attr = DummyAttributes()

			nn = 1
			nn_first = 1

			for index, key in enumerate(obj_data):

				# create new object for item container

				d_name = "vdomclass_%d" % index
				obj = DummyObject(source_object, d_name, e2vdom.virtual_context, source_object, index)
				obj_id = obj.id
				self.tmp_list.append(obj_id)

				for a in source_object_attr:
					obj.set_attribute_ex(source_object_attr[a].name, source_object_attr[a].value, False)
				obj.set_attribute_ex('data', obj_data[key], False)

				obj.actions = copy.copy(source_object.actions)

				res = self.create_dupes_recurse(application, result, obj, source_object.get_objects_list())

				if nn_first == 1:
					nn_first = 0
					classfirst = u"%s-item-first" % classname
				else:
					classfirst = ''

				if nn == len(obj_data):
					classlast = u"%s-item-last" % classname
				else:
					classlast = ''

				if nn % 2:
					classodd = ''
				else:
					classodd = u"%s-item-odd" % classname

				item_data_id = key
				item_data_a = obj_data[key]

				item_data = json.dumps(item_data_a.get('data', "{}"))
				item_x = str(item_data_a.get('x', '0'))
				item_y = str(item_data_a.get('y', '0'))

				obj.set_attribute_ex("visible", "1", False)
				obj.set_attribute_ex("width", source_object_attr['width'].value, False)
				obj.set_attribute_ex("height", source_object_attr['height'].value, False)
				obj.set_attribute_ex("data", item_data, False)
				obj.set_attribute_ex("dataid", item_data_id, False)
				obj.set_attribute_ex("positioning", "1", False)
				obj.set_attribute_ex("left", item_x, False)
				obj.set_attribute_ex("top", item_y, False)
				obj.set_attribute_ex("classauto", "%(class)s-item %(class)s-item-%(nn)s %(classfirst)s %(classlast)s %(classodd)s" % {
					 "class":      classname
					,"nn":         str(nn)
					,"classfirst": classfirst
					,"classlast":  classlast
					,"classodd":   classodd
				}, False)
				obj.set_attribute_ex("classname", "%(userdef)s" % {
					 "name":       obj.name
					,"userdef":    source_object_attr["classname"].value
				}, False)

				result += managers.engine.render(application.itself, obj, object, "vdom")

				nn += 1

		return result


	def __init__(self, id):
		VDOM_object.__init__(self, id)


	def render(self, parent, contents = ""):

		woid = (self.id).replace('-', '_')
		id = u"o_" + woid

		try:
			obj_data = json.loads(self.data, object_pairs_hook = collections.OrderedDict)
		except:
			obj_data = {}

		if isinstance(obj_data, list):
			nod = {}
			for x in obj_data:
				nod[x] = {}
			obj_data = nod

		inner_render = self.do_compile(application.objects.search(self.id), obj_data)

		display = u" display:none; " if self.visible == "0" else u""

		e2vdom.process(self, parent)

		if self.overflow == "1":
			overflow = u"hidden"
		elif self.overflow == "2":
			overflow = u"scroll"
		elif self.overflow == "3":
			overflow = u"visible"
		else:
			overflow = u"auto"

		height = u"height: %spx;" % self.height

		lines_color = u"#%s" % self.linescolor
		lines_color_hover = u"#%s" % self.linescolorhover
		lines_width = u"%s" % int("0%s" % (self.lineswidth).strip())

		style = \
			u"""{display} z-index: {zind}; position: absolute; top: {top}px; left: {left}px;
				width: {width}px; overflow: {overflow}; {height}"""\
			.format(
				display  = display,
				zind     = self.zindex,
				top      = self.top,
				left     = self.left,
				overflow = overflow,
				width    = self.width,
				height   = height)

		classname = u"""class="%s" """ % self.classname if self.classname else u""

		classname_item = 'conn' if self.classname == '' else (self.classname).replace(' ', '').replace('"', '')

