		except:
			connects_array = False
			debug('[connector] ERROR parse defaultconnects JSON: %s' % self.defaultconnects)

		if isinstance(connects_array, list):
			for x in connects_array:
				lbl_tmp = u"%s" % x.get('label', '').replace('"', '&quot;').strip()
				lbl = ''
				if lbl_tmp <> "":
					#lbl = u""",overlays:[["Label",{label:"%s"}]]""" % lbl_tmp
					lbl = u',label:"%s"' % lbl_tmp

				clr_tmp = u"%s" % x.get('color', '').replace('"', '').strip()
				clr = ''
				if clr_tmp <> "":
					clr = u',paintStyle:{strokeStyle:"%s"}' % clr_tmp

				#connects += u""" x.connect({source:"conn-%(source)s",target:"conn-%(target)s" %(label)s },ourConnector /*,%(skin)s*/); """ % {
				connects += u""" x.connect({source:"conn-%(source)s",target:"conn-%(target)s" %(clr)s %(label)s },ourConnector); """ % {
						"source": x.get('source', '').replace('"', '&quot;'),
