	def wysiwyg(self, parent, contents=""):

		html_items = u""
		datalen = 0
		if self.data != "":
			try:
				obj_data = json.loads(tuple(self.data), object_pairs_hook = collections.OrderedDict)
			except:
				obj_data = {}
			datalen = len(obj_data)
			nn = 0
			for key in obj_data:
				nn += 1
				item_data_id = key
				item_data = obj_data[key]
				b, tail = item_data[:0], item_data
				while len(tail)>2:
					b += tail[:2] + "&shy;"
					tail = tail[2:]
				data = b + tail
				html_items += u"<div><b>{nn}</b> <i>{id}</i><br />{data}</div>".format( nn = nn, id = item_data_id, data = data )

		html = u"""
		<style type="text/css">
		.conn_container {
			postition: relative;
			margin: 4px 4px 0 4px;
		}
			.conn_container div {
				background: #ddd;
				border: 1px solid #ccc;
				color: #777;
				float: left;
				font-size: 10px;
				height: 55px;
				overflow: hidden;
				margin: 0 1px 1px 0;
				padding: 1px;
				white-space: wrap;
			}
			.conn_container div b {
				font-color: #333;
			}
			.conn_container div i {
				color: #222;
				font-style: normal;
				font-size: 12px;
			}
		</style>
		<div class="conn_container">%(items)s</div>
		""" % { "items": html_items }

		html = "<![CDATA[" + html + "]]" + ">"

		result = \
			u"""<container id="{id}" visible="{vis}" zindex="{zind}" hierarchy="{hierarchy}" order="{order}"
						top="{top}" left="{left}" width="{width}" height="{height}">
					<svg>
						<rect x="0" y="0" width="{width}" height="{height}" fill="#EEEEEE"/>
					</svg>{contents}
					<text textalign="right" fontweight="bold" top="{ttop}" left="{tleft}" color="#aaaaaa">Connector</text>
					<htmltext top="2" left="2" width="{hwidth}" height="{hheight}">{html}</htmltext>
				</container>
			""".format(
					id = self.id, vis = self.visible,
					zind = self.zindex, hierarchy = self.hierarchy, order = self.order,
					top = self.top, left = self.left,
					width = self.width, height = self.height,
					contents = "" #contents
					,tleft   = int(self.width) - 130
					,ttop    = int(self.height) - 24
					,hwidth  = int(self.width) - 8
					,hheight = int(self.height) - 24
					,html    = html
				)

		return VDOM_object.wysiwyg(self, parent, contents=result)
