import json

class VDOM_datatable_v3(VDOM_object):

	def render(self, parent, contents=""):

		id = "o_" + (self.id).replace('-', '_')
		display = u" display:none; " if self.visible == "0" else u""

		style = u"""{display}z-index: {zind}; position: absolute; top: {top}px; left: {left}px; height: {height}px; width: {width}px"""\
				.format(display = display, zind = self.zindex, top = self.top, left = self.left, height = self.height, width = self.width)

		cssclass = "class = \"%s\"" % self.cssclass if self.cssclass else ""

		if self.header:
			try:
				header = json.loads(self.header)
				if isinstance(header, int):
					raise Exception (u"Incorrect value in header %s" % self.header) 
			except:
				raise Exception (u"Incorrect value in header %s" % self.header)
		else:
			header = []
		if self.data:
			try:
				src_data = json.loads(self.data)
				if isinstance(src_data, int):
					raise Exception (u"Incorrect value in data %s" % self.data)
			except:
				raise Exception (u"Incorrect value in data %s" % self.data)
		else:
			src_data = []

		try:
			key = json.loads(self.key) #self.key if isinstance(self.key, unicode) else json.loads(self.key)
		except:
			key = self.key

		if self.hiddenfields:
			try:
				hidden = json.loads(self.hiddenfields)
				if isinstance(hidden, int):
					hidden = [hidden]
			except:
				if self.hiddenfields in header:
					hidden = [self.hiddenfields]
				else:
					raise Exception (u"Incorrect value in hidden fields  %s" % self.hiddenfields)
		else:
			hidden = []

		data = []
		keys = []
		for value in src_data:
			if isinstance(value, list) and len(header) == len(value):
				row = dict(zip(header, value))
				data.append(row)
				if key and key in header:
					keys.append(row[key])
			else:
				data.append(value)
				if key and key in header:
					keys.append(value[key]) 


		js_headerselect = u"""
$("#"""+id+""" table tr th:not([checkbox])").click(function(){
var hname_"""+id+""" = $(this).html();
execEventBinded(id_"""+id+""".substring(2, id_"""+id+""".length), "headerselected", {headername:hname_"""+id+"""});
 });		
"""

		js_multiselect = u"""
window.selectionmode_"""+id+""" = 'multi';
$("#"""+id+""" table tr th input:checkbox").click(function(){
	if(this.checked){
		$("#"""+id+""" table tr td input:checkbox").attr('checked', 'checked').parent().parent().addClass('row_selected').each(function(i, e){
		  params_"""+id+""".push(window.keys_"""+id+"""[i]);
		  });

	}else{		
		$("#"""+id+""" table tr td input:checkbox").removeAttr('checked').parent().parent().removeClass('row_selected');
		params_"""+id+""" = [];
	}
	execEventBinded(id_"""+id+""".substring(2, id_"""+id+""".length), "rowsselected", {keyList:params_"""+id+"""});
  });

$("#"""+id+""" table tr td input:checkbox").click(function(){
	if(this.checked){
		$(this).parent().parent().addClass('row_selected');
	}else{
		$(this).parent().parent().removeClass('row_selected');
	}
	params_"""+id+""" = [];
	$("#"""+id+""" table tr td input:checkbox").each(function(i, e){
		if (this.checked){
		   params_"""+id+""".push(window.keys_"""+id+"""[i]);
		}
	});
	if($("#"""+id+""" table tr td input:checkbox").size() == $("#"""+id+""" table tr td input:checked").size()){
		$("#"""+id+""" table tr th input:checkbox").attr('checked', 'checked');
	}else{
		$("#"""+id+""" table tr th input:checkbox").removeAttr('checked');
	}
	execEventBinded(id_"""+id+""".substring(2, id_"""+id+""".length), "rowsselected", {keyList:params_"""+id+"""});
  });
"""

		js_cellclick = u"""
$("#"""+id+""" table tr[row] td:not([checkbox])").click(function(){
	var cell_data = $(this).html();
	var cell_index = $(this).attr("index");
	var header_data = header_""" + id + """[cell_index - 1];
	var param = window.keys_"""+id+"""[($(this).parent().attr("index"))-1];
	params_"""+id+"""[0] = window.keys_"""+id+"""[($(this).parent().attr("index"))-1];
	if(window.selectionmode_"""+id+""" != 'multi'){
		$("#"""+id+""" table tr[row]").each(function(){
			if($(this).hasClass('row_selected')){
				$(this).removeClass('row_selected');
			}
		});
		$(this).parent().addClass('row_selected');
		execEventBinded(id_"""+id+""".substring(2, id_"""+id+""".length), "rowsselected", {keyList:params_"""+id+"""});
	}
	execEventBinded(id_"""+id+""".substring(2, id_"""+id+""".length), "cellclick", {keyField:param, cellData:cell_data, headerData:header_data});
  });
"""

		tdata = u""
		theader = u""
		vheader = [h for h in header if h not in hidden]
		if vheader:
			theader = u'<tr header class="thead"><th checkbox class="th-cell th-cell-0"><input type="checkbox"></th>\n' if self.selectionmode == "1" else u'<tr header class="thead">'
			for (index, colname) in zip(range(len(vheader)), vheader):
				theader += u'<th index="'+unicode(index+1)+'" class="th-cell th-cell-'+unicode(index+1)+'">' + unicode(colname) + u'</th>\n'
			theader += u'</tr>'
		for (row_i, row) in zip(range(len(data)), data):
			if (row_i + 1) % 2:
				tdata += u'<tr row index="'+unicode(row_i+1)+'" class="row row-'+unicode(row_i+1)+' oddrow">\n'
			else:
				tdata += u'<tr row index="'+unicode(row_i+1)+'" class="row row-'+unicode(row_i+1)+' even">\n'

			tdata += u'<td checkbox align="center" class="cell cell-0"><input type="checkbox"></td>\n' if self.selectionmode == "1" else ""
			if vheader:
				for (index, col) in zip(range(len(vheader)), vheader):
					try:
						tdata += u'<td index="'+unicode(index+1)+'" class="cell cell-'+unicode(row_i+1)+'_'+unicode(index+1)+' cell-'+unicode(index+1)+'">' + unicode(row[col]) + u'</td>\n'
					except:
						raise Exception ("In row (%s) no column %s"%(json.dumps(row), col))
			else:
				for (index, val) in zip(range(len(row)), row):
					tdata += u'<td class="cell cell-'+unicode(index+1)+'">' + unicode(val) + u'</td>\n'
			tdata += u"</tr>\n"

		select = js_multiselect + js_cellclick if self.selectionmode == "1" else js_cellclick
		result = u"""<script type="text/javascript">
jQuery(document).ready(function($){
var id_"""+id+""" = '"""+id+"""';
window.keys_"""+id+""" = """ + json.dumps(keys) + """;
window.header_"""+id+""" = """ + json.dumps(vheader) + """
var params_"""+id+""" = [];
%(select)s
%(headerselect)s
});
</script>		
<style type="text/css">
%(css)s
</style>
<div id="%(id)s" style="%(style)s" %(class)s>
<table width="100%%" height="100%%" border="2" cellpadding="5" class="table">
<caption>%(title)s</caption>
%(theader)s
<tbody class="tbody">
%(tdata)s
</tbody>
</table>
</div>
""" % {"select": select,
	   "headerselect": js_headerselect,
	   "css": self.style % {"id": id},
	   "id": id,
	   "style": style,
	   "class": cssclass,
	   "title": self.title,
	   "theader": theader if self.showheader == '1' else '',
	   "tdata": tdata}

		return VDOM_object.render(self, parent, contents=result)

	def wysiwyg(self, parent, contents=""):
		try:
			header = json.loads(self.header)
		except:
			header = []
		try:
			src_data = json.loads(self.data)
		except:
			src_data = []

		data = []
		theader = ""
		tdata = ""
		for value in src_data:
			if isinstance(value, list) and len(header) == len(value):
				row = dict(zip(header, value))
				data.append(row)
			else:
				data.append(value)

		for colname in header:
			theader += u"<cell backgroundcolor=\"#f0f0f0\" bordercolor=\"#000000\" borderwidth=\"1\"><text fontsize=\"14\" color=\"#000000\" textalign=\"center\">"+unicode(colname)+u"</text></cell>"

		for row in data:
			tdata += u"<row backgroundcolor=\"#f0f0f0\" bordercolor=\"#000000\" borderwidth=\"1\">"
			if header and len(header) == len(row):
				for col in header:
					tdata += u"<cell backgroundcolor=\"#f0f0f0\" bordercolor=\"#000000\" borderwidth=\"1\"><text fontsize=\"12\" color=\"#000000\" textalign=\"center\">"+unicode(row[col])+u"</text></cell>"
			else:
				for val in row:
					tdata += u"<cell backgroundcolor=\"#f0f0f0\" bordercolor=\"#000000\" borderwidth=\"1\"><text fontsize=\"12\" color=\"#000000\" textalign=\"center\">"+unicode(val)+u"</text></cell>"

			tdata +=u"</row>"

		result=u"<container id=\"%s\" zindex=\"%s\" hierarchy=\"%s\" top=\"%s\" left=\"%s\" width=\"%s\" height=\"%s\">"\
					u"<table zindex=\"%s\" top=\"0\" left=\"0\" width=\"%s\" height=\"%s\" backgroundcolor=\"#f0f0f0\" bordercolor=\"#000000\" borderwidth=\"1\" >"\
						u"<row backgroundcolor=\"#f0f0f0\" bordercolor=\"#000000\" borderwidth=\"1\">%s"\
						u"</row>"\
						u"%s"\
					u"</table>"\
				u"%s</container>"%(
					self.id, self.zindex, self.hierarchy, self.top, self.left, self.width, self.height,
					self.zindex, self.width, self.height,
					theader,
					tdata,
					contents)


		return VDOM_object.wysiwyg(self, parent, contents=result)



def set_attr(app_id, object_id, param):

	css = """#%(id)s caption {
font: 18px Arial;
text-align:left;
margin-bottom:15px;

}

#%(id)s table.table {
border-collapse:collapse;
border:1px solid #d4d4d4;
height:0px;
}

#%(id)s .th-cell {
height:32px !important;
background:#f5f5f5;
text-align:left;
padding-left:10px;
border-bottom:2px solid #d4d4d4;
}

#%(id)s .thead {
height:32px !important;
background:#f5f5f5;
}

#%(id)s table.table td.cell {
height:32px !important;
text-align:left;
padding-left:10px;
}
#%(id)s tr.even {
background: #f0f0f0; 
} 
#%(id)s .row {
cursor:pointer;
}
#%(id)s .row_selected {
background:#effdff !important;
}
"""	

	o = application.objects.search(object_id)

	# set attributes

	if "skin" in param:
		if param["skin"]["value"] == "1":
			o.set_attributes({"style": css})
	if "style" in param and param["style"]["value"] and o.attributes.style != css:		
		o.set_attributes({"skin": 0}) 	

	return ""

