import e2vdom

def on_compile(application, object, action_name, context, objects):
	result=objects
	for xobject in object.get_objects_list():
		result.append({"object": xobject})
	return result

class VDOM_dialog_2(VDOM_object):

	def render(self, parent, contents=""):

		idn = (self.id).replace('-', '_')
		id = u'o_' + idn

		e2vdom.process(self, parent)

		result = u"""<style type="text/css">%(css)s</style>""" % {"css": self.style}

		if self.draggable == "0":
			draggable = "true"
		else: draggable = "false"

		if self.show == "1":
			show = "true"
		else: show = "false"

		if self.modal == "0":
			modal = "true"
		else: modal = "false"

		result += """<table style='display:none' cellspacing='0' cellpadding='0' border='0' class='boxy-wrapper'>
			<tr><td class='top-left'></td><td class='top'></td><td class='top-right'></td></tr>
			<tr><td class='left'></td><td class='boxy-inner'>
				<div id='%(id)s' class='%(classname)s' style='position: relative; width: %(width)spx; height: %(height)spx; z-index: %(zindex)s'>%(contents)s</div>
			</td><td class='right'></td></tr>
			<tr><td class='bottom-left'></td><td class='bottom'></td><td class='bottom-right'></td></tr></table>""" % {
			"id": id, "width": self.width, "height": self.height, "zindex": self.zindex, "contents": contents, "classname": self.classname
			}

		result += """<script type='text/javascript'>
jQuery(document).ready(function($){
	/* ver 2011-12-06 */
	dialog_win_%(id)s=new Boxy($('#%(id)s'),{
		width: %(width)s
		,height: %(height)s
		,draggable: %(drag)s
		,title: '%(title)s'
		,closeText: ''
		,show: %(show)s
		,modal: %(modal)s
		,hideShrink: false
		,afterHide: function(){ execEventBinded("%(idn)s", "hide", {}); $('#ui-datepicker-div').fadeOut(); }
		,afterShow: function(){ execEventBinded("%(idn)s", "show", {}); }
	});
	if(typeof pngfix=='function'){
		$('.boxy-wrapper').find('.top,.left,.right,.bottom,.top-right,.top-left,.bottom-left, .bottom-right').pngfix({sizingMethod:'scale'});
	}
});
</script>""" % {
			"id": id, "idn": idn, "width": self.width, "height": self.height, "drag": draggable,
			"title": self.title, "show": show, "modal": modal }

		return VDOM_object.render(self, parent, contents=result)

	def wysiwyg(self, parent, contents=""):

		result= "<container id=\"%s\" zindex=\"%s\" hierarchy=\"%s\" order=\"%s\" top=\"%s\" left=\"%s\" width=\"%s\" height=\"%s\">"\
					"<svg>"\
						"<rect x=\"%s\" y=\"%s\" width=\"%s\" height=\"%s\" stroke=\"#cccccc\" stroke-width=\"3\" fill=\"#ffffff\">"\
							"<rect x=\"%s\" y=\"%s\" width=\"%s\" height=\"40\" fill=\"#e9e9e9\"/>"\
						"</rect>"\
					"</svg>"\
					"<text top=\"10\" width=\"%s\" fontsize=\"14\" color=\"black\" textalign=\"center\">%s</text>"\
				"%s</container>"%(
					self.id, self.zindex, self.hierarchy, self.order, self.top, self.left, int(self.width) + 3, int(self.height) + 3,
					0, 0, self.width, self.height,
					0, 0, self.width,
					self.width, self.title,
					contents)

		return VDOM_object.wysiwyg(self, parent, contents=result)

