class CachedDict(dict):

    def __init__(self, items, compute):
        super(CachedDict, self).__init__()
        self.initial_items = items
        self.computed_items = {}
        self.compute = compute

    def __getitem__(self, key):
        if key not in self.computed_items:
            self.computed_items[key] = self.compute(self.initial_items[key])

        return self.computed_items[key]


def _unicode(value):
    if isinstance(value, unicode):
        return value

    if isinstance(value, str):
        return value.decode('utf8')

    return unicode(value)


def xml_node_attrs(xml):
    toplvl = xml.split('>', 1)[0]
    attrs = [attr.split("=",1) for attr in toplvl.split(' ') if '=' in attr]
    return {key.lower(): value[1: len(value)-1] for key, value in attrs}


class xmlencoding(object):
    _re = None

    @classmethod
    def get_re(self):
        if self._re is None:
            illegal_unichrs = [
                (0x00, 0x08), (0x0B, 0x1F), (0x7F, 0x84), (0x86, 0x9F),
                (0xD800, 0xDFFF), (0xFDD0, 0xFDDF), (0xFFFE, 0xFFFF),
                (0x1FFFE, 0x1FFFF), (0x2FFFE, 0x2FFFF), (0x3FFFE, 0x3FFFF),
                (0x4FFFE, 0x4FFFF), (0x5FFFE, 0x5FFFF), (0x6FFFE, 0x6FFFF),
                (0x7FFFE, 0x7FFFF), (0x8FFFE, 0x8FFFF), (0x9FFFE, 0x9FFFF),
                (0xAFFFE, 0xAFFFF), (0xBFFFE, 0xBFFFF), (0xCFFFE, 0xCFFFF),
                (0xDFFFE, 0xDFFFF), (0xEFFFE, 0xEFFFF), (0xFFFFE, 0xFFFFF),
                (0x10FFFE, 0x10FFFF)
            ]

            illegal_ranges = ["%s-%s" % (unichr(low), unichr(high)) for (low, high) in illegal_unichrs if low < sys.maxunicode]
            self._re = re.compile(u"[%s]" % u"".join(illegal_ranges))

        return self._re

    @classmethod
    def escape(self, text):
        return self.get_re().sub("", text)


class DummyAttribute(object):

    __slots__ = ("name", "value")

    def __init__(self, name, value=0):
        self.name = name
        self.value = value


class DummyAttributes(object):

    __slots__ = ("map",)

    def __init__(self):
        self.map = {}

    def __getitem__(self, key):
        if key == "name":
            return DummyAttribute("name", "")

        elif key == "dynamic":
            return DummyAttribute("dynamic",1)

        if key not in self.map:
            self.map[key] = DummyAttribute(key)

        return self.map[key]

    def values(self):
        return self.map.values()


class DummyObject(object):

    def __init__(self, vdomtype, name, action_name):
        self.name = name
        self.actions = {"name": {}, "id": {}}
        self.attributes = DummyAttributes()
        self.containers = {}
        self.dynamic = {(action_name, self.name): 1}
        self.id = str(uuid.uuid4())
        self.libraries = {}
        self.objects = {}
        self.objects_list = []
        self.objects_by_name = {self.name: self}
        self.order = 0
        self.type = vdomtype
        self.types = {self.type.id: self.type}

    def get_attributes(self):
        return self.attributes

    def set_attribute_ex(self, name, value, x=0):
        old_value = self.attributes[name].value
        self.attributes[name].value = value
        return {
            "value": value,
            "old_value": old_value
        }

    def get_objects_list(self):
        return self.objects_list

    def get_objects(self):
        return self.objects

    def add_child(self, child):
        self.objects_by_name[child.name] = child
        self.objects_list.append(child)
        self.objects[child.id] = child


class VDOMTemplate(object):

    __slots__ = (
        "attributes",
        "vdomxml"
    )

    def __init__(self, xml, attrs):
        self.attributes = attrs
        self.vdomxml = xml

    def __getitem__(self, key):
        return self.attributes.get(key.lower(), '')

    def __setitem__(self, key, value):
        self.attributes[key.lower()] = _unicode(value)

    def __delitem__(self, key):
        del self.attributes[key.lower()]

    def tohtml(self, parent):
        return render(parent, self.vdomxml, self.attributes)


class VDOMLayout(object):

    __slots__ = (
        "objects",
        "left",
        "top",
        "width",
        "height",
        "margin"
    )

    def __init__(self):
        self.objects  = []
        self.left = 0
        self.top = 0
        self.width = 0
        self.height = 0
        self.margin = 0

    @property
    def right(self):
        return self.left + self.width

    @property
    def bottom(self):
        return self.top + self.height

    def arrange(self, element):
        pass

    def append(self, obj):
        self.objects.append(obj)
        if not isinstance(obj, (str,unicode)):
          self.arrange( obj )

    def _abs(self, obj):
        left = int(obj['left'] or 0)
        top = int(obj['top'] or 0)
        obj['left'] = left + self.left
        obj['top'] = top + self.top

    def tohtml(self, parent):
        # make absolute
        r = []
        for obj in self.objects:
            if not isinstance(obj, (str,unicode)):
              self._abs(obj)
              r.append(obj.tohtml(parent))

            else:
              r.append(obj)

        return ''.join(r)


class VDOMVLayout(VDOMLayout):

    def __init__(self, margin=0):
        super(VDOMVLayout, self).__init__()
        self.margin = margin

    def arrange(self, obj):
        margin = self.margin if len(self.objects) == 0 else 0

        obj['left'] = 0
        obj['top'] = self.bottom + margin - self.top

        width = int(obj['width'] or 0)
        height = int(obj['height'] or 0)

        self.width = max(self.width, width)
        self.height += self.margin + height


class VDOMHLayout(VDOMLayout):

    def __init__(self, container=None):
        super(VDOMHLayout, self).__init__()
        self.width = container.height if container else 0

    def arrange(self, obj):
        obj['left'] = self.right + self.margin
        obj['top'] = self.top

        width = int(obj['width'] or 0)
        height = int(obj['height'] or 0)

        self.width += self.margin + width
        self.height = max(self.height, height)


def render(parent, vdomxml, data=None):
    data = data or {}
    dummies = create_dummies(parent, vdomxml, True, data)

    for child_id in dummies:
        application.itself.all_dynamic_objects[child_id] = dummies[child_id]

    result = managers.engine.render(application.itself, dummies["initial"], parent, "vdom")

    # HACK !!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!
    obj = dummies["initial"]
    name = 'Obj_%s' % obj.id.replace('-', '_')
    result += """\
<script>var %s = {
    "eventEngine" : {
        "eventDispatcher" : {
            "eventArray" : []
        }
    }
}</script>""" % name

    for child_id in dummies:
        if child_id in application.itself.all_dynamic_objects:
            del application.itself.all_dynamic_objects[child_id]

    return result


def create_dummies(parent, vdomxml, initial=False, data=None):
    data = data or {}
    objects = {}
    root_props = {}

    for key, value in data.iteritems():
        key = key.lower()
        if '.' in key:
            continue
        
        root_props[key] = value

    for key in root_props:
        del data[key]

    attr_map={}
    type_obj = managers.xml_manager.get_type_by_name(vdomxml.lname)
    for aname in type_obj.get_attributes():
        attr_map[aname.lower()] = type_obj.get_attributes()[aname].default_value

    # parse attributes
    for attr_name in vdomxml.attributes:
        if attr_name in attr_map or "name" == attr_name:
            attr_map[attr_name] = vdomxml.attributes[attr_name]
        
        else:
            raise Exception("Incorrect attribute: %s" % attr_name)

    for child in vdomxml.children:
        if "attribute" == child.lname:
            attr_name = child.attributes["name"]

            if not attr_name:
                continue

            if attr_name in attr_map:
                attr_map[attr_name] = child.get_value_as_xml()
            
            else:
                raise Exception("Incorrect attribute: %s" % attr_name)

        source_object_attr = DummyAttributes()

    # change value of attributes that was updated
    for aname in attr_map:
        if aname in root_props:
            attr_map[aname] = root_props[aname]

    obj = DummyObject(type_obj, attr_map["name"], e2vdom.virtual_context)
    if initial:
        objects["initial"] = obj

    else:
        parent.add_child(obj)

    objects[obj.id] = obj
    for a in attr_map:
        obj.set_attribute_ex(a, attr_map[a])

    # process all child objects
    for child in vdomxml.children:
        if "attribute" == child.lname:
            continue

        name = child.attributes['name'] + '.' # include separator
        n = len(name)
        child_data = {}

        for key,value in data.iteritems():
            if key[:n] != name: continue # skip other objects
            child_data[ key[n:] ] = value

        objects.update(create_dummies(obj, child, False, child_data))

    return objects


