        } else if (typeof method == 'object' || ! method ) {
            return methods.init.apply(this, arguments);
        } else {
            $.error("Dynamic object view: unknown method " + method);
        }
    };
}(jQuery));
</script>"""


class VDOM_dynobjectview(VDOM_object):

    def wysiwyg(self, parent, contents=""):
        image_id = "76bfc7be-dbe3-46e3-8d11-cc78a576b63a"
        result = utils_wysiwyg.get_empty_wysiwyg_value(self, image_id)
        return VDOM_object.wysiwyg(self, parent, contents=result)

    def parse_properties(self):

        try:
          data = json.loads(self.data, object_pairs_hook=OrderedDict)
        except Exception:
          if self.data:
            raise Exception("Can't parse 'data'")

          data = []

        try:
          templates = json.loads(self.template)
        except Exception:
          if self.template:
            raise Exception("Can't parse 'template'")

          templates = {}

        try:
          bindings = json.loads(self.bindings)
        except Exception:
          if self.bindings:
            raise Exception("Can't parse 'bindings'")

          bindings = {}

        return data, templates, bindings

    def generate_layout(self):

        data, templates, bindings = self.parse_properties()
        layouts = (VDOMVLayout, VDOMHLayout, VDOMVLayout) 
        
        xml_templates = CachedDict(templates, lambda xml: xml_object(srcdata=xml.encode("utf-8")))
        template_attrs = CachedDict(templates, lambda template: xml_node_attrs(template))

        dynlayout = layouts[int(self.layout)]()
        dynlayout.margin = 17

        if not (data and templates and bindings):
            return dynlayout

        # css classes
        first_cls = u"dov-item-first "
        last_cls = u"dov-item-last "
        odd_cls = u"dov-item-odd "

        index = 1
        is_first = True

        only_div = isinstance(data, list)
        vdomclass_width = ''
        vdomclass_height = ''

        if only_div:
            vdomclass_width = template_attrs["default"]["width"]
            vdomclass_height = template_attrs["default"]["height"]

        for item in data.iteritems():

            only_div_1 = False
            obj_id = ''

            if not only_div:
                if isinstance(item[1], (str, unicode)):
                    only_div_1 = True

                    obj_id, obj_template = item
                    vdomclass_width = template_attrs[obj_template]["width"]
                    vdomclass_height = template_attrs[obj_template]["height"]

                else:
                    only_div_1 = False

            else:
                obj_id = item

            classes_str = first_cls if is_first else ''
            classes_str += last_cls if index == len(data) else ''
            classes_str += odd_cls  if index % 2 == 0 else ''

            classes_str = " {classes}dov-item dov-item-{index} {notloaded}".format(
                classes=classes_str,
                index=index,
                notloaded="not-loaded" if only_div or only_div_1 else ""
            )

            if only_div or only_div_1:

                styleforitem = u"position: relative;width: %spx;height: %spx;display: block;" % (vdomclass_width, vdomclass_height)
                dynlayout.append(u'<div class="%(classes)s" dataid="%(id)s" style="%(style)s"></div>' % {
                    "id": obj_id,
                    "classes": classes_str,
                    "style": styleforitem
                })

            else:
                obj_id, item = item

                tname = item.get('vdomclass', 'default')
                vtmp = VDOMTemplate(xml_templates[tname], template_attrs[tname].copy())
            
                binding = bindings.get(tname, {})

                for key in item:
                    if key not in binding:
                        continue

                    attrs = binding[key]
                    if not isinstance(attrs, list):
                        attrs = [attrs]

                    for a in attrs:
                        vtmp[a] = xmlencoding.escape(item[key])

                vtmp["name"] = "dovtemplate_%d" % index 

                vtmp["classname"] = vtmp["classname"] + classes_str
                vtmp["dataid"] = obj_id

                dynlayout.append(vtmp)

            index += 1
            is_first = False

        return dynlayout

    def compile_style(self):

        overflow = {
            "1": "hidden",
            "2": "scroll",
            "3": "visible",
        }

        layout = {
            "0": "clear: both",
            "1": "float: left;",
            "default": "width: 100% !important; clear: both;",
        }

        obj_style = u"""{display}z-index: {zind};position: {pos};top: {top}px;left: {left}px;height: {height}px;width: {width}px;overflow: {overflow};""".format(
            display=u"display:none;" if self.visible == "0" else u"",
            zind=self.zindex,
            pos=self.position,
            top=self.top,
            left=self.left,
            width=self.width,
            height=self.height,
            overflow=overflow.get(self.overflow, "auto"),
        )
          
        dom_id = "o_" + self.id.replace('-', '_')

        items_style = u"""<style type="text/css">
{user_css}
#{dom_id} .dov-item {{
    {layout}
}}</style>""".format(
            dom_id=dom_id,
            user_css=self.css % {"id": dom_id},
            layout=layout.get(self.layout, "default"),
        )

        return obj_style, items_style

    def render(self, parent, contents=""):

        dynlayout = self.generate_layout()

        obj_id = self.id.replace('-', '_')
        dom_id = u"o_" + obj_id
        
        if request.render_type == "e2vdom" and self.dynamicrender == "1":

            result = u"""<noreplace/>
<div id="{tmp_id}" style="display:none">{contents}</div>
<script type='text/javascript'>
$(document).ready(function(){{
  var x = $('#{dom_id}>div.dov-content');
  var dovtmp = $('#{tmp_id}:last');
  if ($.trim(dovtmp.html()) != '') {{
      $("#{dom_id}").dynamicObjectView("insertHTMLAtEnd", dovtmp);
  }}
}});

</script>""".format(
                dom_id=dom_id,
                tmp_id=str(uuid.uuid4()),
                contents=dynlayout.tohtml(self),
            )

            return VDOM_object.render(self, parent, contents=contents + result)

        else:

            options = {
                "id": obj_id,
                "itemClickLoading": self.loading,
                "dragndrop": self.draggable == "1",
                "dragnsort": self.sortable == "1",
                "dragndropHelper": self.draghelper if self.draghelper else "clone",
                "dynamicLoading": True,
            }

            obj_style, items_style = self.compile_style()

            result = u"""{style_items}<div name="{name}" id="{dom_id}" style="{obj_style}" class="{classes}">
        <div class='dov-content'>
            {contents}
            <span class='dov-eoi' style='display:block;clear:both'></span>
        </div>
    </div>
    <script>jQuery(document).ready(function(){{jQuery("#{dom_id}").dynamicObjectView({options});}});</script>"""
            contents += GLOB_JS + result.format(
                name=self.name,
                style_items=items_style,
                dom_id=dom_id,
                obj_style=obj_style,
                classes=self.classname,
                contents=dynlayout.tohtml(self),
                options=json.dumps(options)
            )

            return VDOM_object.render(self, parent, contents=contents)
