    def render(self, parent, contents=""):

        try:
          data = json.loads(self.data)
        except Exception:
          raise Exception("Can't parse 'data'")

        try:
          templates = json.loads(self.template)
        except Exception:
          raise Exception("Can't parse 'template'")

        try:
          bindings = json.loads(self.bindings)
        except Exception:
          raise Exception("Can't parse 'bindings'")

        layouts = { "0": VDOMVLayout,
                    "1": VDOMHLayout,
                    "2": VDOMVLayout
        }

        dynlayout = layouts[self.layout]()
        dynlayout.margin = 17

        classname = u"dov" if self.classname == "" else self.classname
        nn = 1
        nn_first = 1
        styleforitem = ''
        # add items to layout

        for item in data:
            vdom_class = item['vdomclass']
            template = VDOMTemplate()
            template.vdom_xml = templates[vdom_class]
            binding = bindings.get(vdom_class, {})

            for key in item:
                if key not in binding:
                    continue

            attrs = binding[key]
            if not isinstance(attrs, list):
                attrs = [attrs]

            for a in attrs:
                template[a] = xmlencoding.escape(item[key])

            template["name"] = "vdomclass_%d" % nn

            if nn_first == 1:
              classfirst = u"%s-item-first" % classname
            else:
              classfirst = ''

            if nn == len(data):
              classlast = u"%s-item-last" % classname
            else:
              classlast = ''

            if nn % 2:
              classodd = ''
            else:
              classodd = u"%s-item-odd" % classname

            classes = u" %(class)s-item %(class)s-item-%(nn)s %(classfirst)s %(classlast)s %(classodd)s %(notloaded)s" % {
              "class":      classname,
              "nn":         str(nn),
              "classfirst": classfirst,
              "classlast":  classlast,
              "classodd":   classodd,
              "notloaded":  "not-loaded"# if onlydiv else ""
            }

            template["classname"] = template["classname"] + classes
            dynlayout.append(template)

