	for (var x in idlist) {
		$j('>div.dov-content>div[dataid="'+idlist[x].replace('"', '&quot;')+'"]', o).toggleClass('selected');
	}
	vdom_dov_getSelected(o.id.substring(2, o.id.length));
}

if (typeof idlist === 'object') {
	var o = this.domCont;
	for (var x in idlist) {
		$j('>div.dov-content>div[dataid="'+idlist[x].replace('"', '&quot;')+'"]',o).addClass('selected');
	}
	vdom_dov_getSelected(o.id.substring(2, o.id.length));
}

if (typeof idlist === 'object') {
	var o = this.domCont;
	for (var x in idlist) {
		$j('>div.dov-content>div[dataid="'+idlist[x].replace('"', '&quot;')+'"]',o).removeClass('selected');
	}
	vdom_dov_getSelected(o.id.substring(2, o.id.length));
}

if (typeof items === 'object') {
	var o = this.domCont, z = $j('>div.dov-content>div:first',o);
	if (z.size() <= 0) z = $j('>div.dov-content>span.dov-eoi',o);
	for (var x in items) {
		$j(vdom_dov_makeDiv(items[x], o)).insertBefore(z);
	}
	vdom_dov_markShow(o.id.substring(2, o.id.length));
}

if (typeof items === 'object') {
	var o = this.domCont;
	for (var x in items) {
		$j(vdom_dov_makeDiv(items[x], o)).insertBefore($j('>div.dov-content>span.dov-eoi',o));
	}
	vdom_dov_markShow(o.id.substring(2, o.id.length));
}

if (typeof items === 'object') {
	var o = this.domCont, z = $j('>div.dov-content>div['+id+']',o);
	if (z.size() > 0) {
		for (var x in items) {
			$j(vdom_dov_makeDiv(items[x], o)).insertAfter(z);
		}
		vdom_dov_markShow(o.id.substring(2, o.id.length));
	}
}

if (typeof items === 'object') {
	var o = this.domCont, z = $j('>div.dov-content>div['+id+']',o);
	if (z.size() > 0) {
		for (var x in items) {
			$j(vdom_dov_makeDiv(items[x], o)).insertBefore(z);
		}
		vdom_dov_markShow(o.id.substring(2, o.id.length));
	}
}
