import e2vdom

def on_compile(application, object, action_name, context, objects):
	result=objects
	for xobject in object.get_objects_list():
		result.append({"object": xobject})
	return result

class VDOM_form(VDOM_object):

	def render(self, parent, contents=""):
		display = u"display:none; " if self.visible == "0" else u""

		e2vdom.process(self, parent)

		if self.overflow == "1":
			ov = u"hidden"
		elif self.overflow == "2":
			ov = u"scroll"
		elif self.overflow == "3":
			ov = u"visible"
		else:
			ov = u"auto"

		if not self.target or self.meth == "event":
			target = u""
		else:
			ref_obj = application.objects.search(self.target)
			target = u"/%s.vdom" % ref_obj.name if ref_obj else u""

#		if self.submitonce == "1" and self.meth != "event":
#			submitonce = u"""onsubmit="return vdom_ui_formSubmit(this, %s)" """ % self.submitonce
#		else:
		submitonce = u""

		woid = (self.id).replace('-', '_')
		id = u"o_" + woid

		if self.meth == "event":
			submitjs = u"""
			<script type="text/javascript">
				jQuery(function($){
					$('#%(id)s').submit(function(){
						var x=[], a=$('#%(id)s').serializeArray();
						for (k in a) if(typeof a[k].name !== 'undefined' && typeof a[k].value !== 'undefined') x[a[k].name]=a[k].value;
						execEventBinded('%(woid)s', "submit", x);
						return false;
					});
				});
			</script>
			""" % { "id": id, "woid": woid }
		else:
			submitjs = u""

		if not self.enctype or self.meth == "event":
			enctype = u""
		else:
			enctype = u"""enctype="%s" """ % self.enctype

		style = u"overflow:{ovf};z-index:{zind};position:{pos};top:{top}px;left:{left}px;width:{wid}px;height:{hei}px;" \
			.format(ovf = ov, zind = self.zindex, pos = self.position, 
					top = self.top, left = self.left, wid = self.width, hei = self.height)

#		result = u"""<form id="%(id)s" name="%(name)s" style="%(display)s %(style)s" method="%(method)s" 
#							action="/%(target)s.vdom" enctype="%(encode_type)s" 
#							onsubmit="return vdom_ui_formSubmit(this,%(submitonce)s)">%(contents)s
#					</form><script type="text/javascript">%(registrations)s</script>
#				""" % {
#				"submitonce": self.submitonce, "id": id, "name": self.name, 
#				"display": display, "style": style, "method": "post", 
#				"target": target, "encode_type": self.enctype, 
#				"contents": contents, "registrations": registrations }

		result = u"""<form id="{id}" style="display:block;margin:0; {display} {style}" 
							name="{name}" method="{method}" action="{target}" style="overflow:visible" 
							{enctype} {submitonce}>{contents}
					</form> {submitjs} 
				""".format(id = id, display = display, style = style, name = self.name, method = self.meth, target = target,
				enctype = enctype, submitonce = submitonce, contents = contents, submitjs = submitjs )

		return VDOM_object.render(self, parent, contents=result)

	def wysiwyg(self, parent, contents=""):

		result = u"""<container id="{id}" visible="{vis}" zindex="{zind}" hierarchy="{hierarchy}" order="{order}" 
							top="{top}" left="{left}" width="{width}" height="{height}">
						<svg>
							<rect x="0" y="0" width="{width}" height="{height}" fill="#EEEEEE" fill-opacity=".4" />
						</svg>{contents}
					</container>
				""".format(id = self.id, vis = self.visible, zind = self.zindex, 
				hierarchy = self.hierarchy, order = self.order, 
				top = self.top, left = self.left, width = self.width, height = self.height, 
				contents = contents)

		return VDOM_object.wysiwyg(self, parent, contents=result)
