class VDOM_formtexteditor(VDOM_object):

	def render(self, parent, contents=""):
		display = u"display:none;" if self.visible == "0" else u"display:block;"
		id = u"o_" + (self.id).replace('-', '_')			

		style = u"""z-index: {zind}; position: {pos}; top: {top}px; left: {left}px;
					width: {width}px; height: {height}px; padding: 2px 5px 2px 5px; {display} font: 14px tahoma
				""".format(zind = self.zindex, pos = self.position, top = self.top, left = self.left, 
						width = self.width, height = self.height, display = display)

		result = u"""
<script type="text/javascript">$q(document).ready(function($) {
	$('#%(id)s').cleditor();
});</script>
			<textarea id="%(id)s" name="%(name)s" tabindex="%(tabind)s" style="%(style)s">%(value)s</textarea>
			""" % {
				"id":     id,
				"name":   self.name,
				"tabind": self.tabindex,
				"style":  style,
				"value":  self.value
			}

		return VDOM_object.render(self, parent, contents=result)

	def wysiwyg(self, parent, contents=""):
		text_size = self.width if self.value else 0

		result = u"""<container id="{id}" visible="{vis}" zindex="{zind}" hierarchy="{hierarchy}" order="{order}" 
							top="{top}" left="{left}" width="{width}" height="{height}">
						<svg>
							<rect x="0" y="0" width="{rec_wid}" height="{rec_hei}" fill="#FFFFFF" stroke="#000000"/>
						</svg>
						<text top="4" width="{txt_wid}" color="#000000">{value}</text>
					</container>
				""".format(id = self.id, vis = self.visible, zind = self.zindex, 
							hierarchy = self.hierarchy, order = self.order, 
							top = self.top, left = self.left, width = self.width, height = self.height, 
							rec_wid = int(self.width) - 1, rec_hei = int(self.height) - 1,
							txt_wid = text_size, value = self.value)

		return VDOM_object.wysiwyg(self, parent, contents=result)
