import json
import vobject
import datetime
from dateutil.relativedelta import *
import random

class VDOM_fullcalendar(VDOM_object):

	VERSION = "version 12.07"
	"""back to all resources, allDay works, resources work """
	RENDER_DIV = """<div id="%s" style="%s"></div>"""	
	RENDER_SCRIPT = """<script type="text/javascript"> 
	$(document).ready(function() {
		$("#%(id_out)s").fullCalendar({
			header: {
				left: '',
				center: '',
				right: ''
			},
			firstDay: %(first_day)s,
			eventSources: %(event_sources)s,
			eventClick: function(event) {
				var id="%(id_out)s";
				execEventBinded(id.substring(2, id.length), "eventClick", {id:event.id});
			},
			dayClick: function(date) {
				var id="%(id_out)s";
				var year=date.getFullYear();
				var month=date.getMonth() + 1;
				if (month < 10) {
					month = "0" + month.toString();
				}
				var day=date.getDate();
				if (day < 10){
					day = "0" + day.toString();
				}
				var start = year.toString() + '-' + month + '-' + day;
				execEventBinded(id.substring(2, id.length), "dayClick", {"start":start});
			},
			editable: true,
			eventDrop: function(event, dayDelta, minuteDelta, allDay) {
				startDate = event.start;
				//alert( startDate )
				start = startDate.getFullYear() + "-" + 
						(startDate.getMonth() + 1).toString() + "-" + 
						startDate.getDate() + " " + 
						startDate.getHours() + ":" + 
						startDate.getMinutes();
				//alert( event.end )
				var endDate = event.end
				if (event.end == null) {
					endDate = event.start
				}
				else {
					endDate = event.end
				}
				//endDate = event.end;
				//if (endDate == "null") {
					//endDate = startDate;
				//}
				//alert( endDate )
				//alert( event.start )
				end = 	endDate.getFullYear() + "-" + 
						(endDate.getMonth() + 1).toString()  + "-"  + 
						endDate.getDate() + " " + 
						endDate.getHours() + ":" + 
						endDate.getMinutes();
				event.allDay = allDay;
				var id = "%(id_out)s";
				execEventBinded(id.substring(2, id.length), "eventDrag", {id:event.id, start: start, end:end, instance: event.instance, allDay:event.allDay})
			},
			eventResize: function(event) {
				startDate = event.start;
				start = startDate.getFullYear() + "-" + 
						(startDate.getMonth() + 1).toString() + "-" + 
						startDate.getDate() + " " + 
						startDate.getHours() + ":" + 
						startDate.getMinutes();
				var endDate = event.end
				if (event.end == null) {
					endDate = event.start
				}
				else {
					endDate = event.end
				}
				end = 	endDate.getFullYear() + "-" + 
						(endDate.getMonth() + 1).toString()  + "-"  + 
						endDate.getDate() + " " + 
						endDate.getHours() + ":" + 
						endDate.getMinutes();
				var id = "%(id_out)s";
				execEventBinded(id.substring(2, id.length), "eventResize", {id:event.id, start: start, end:end, instance: event.instance, allDay:event.allDay })
			},
			selectable: true,
			select: function(startDate, endDate, allDay) {
				$("#%(id_out)s").fullCalendar('unselect');
				var id = "%(id_out)s";
				end = 	endDate.getFullYear() + "-" + 
						(endDate.getMonth() + 1).toString()  + "-"  + 
						endDate.getDate() + " " + 
						endDate.getHours() + ":" + 
						endDate.getMinutes();

				start = startDate.getFullYear() + "-" + 
						(startDate.getMonth() + 1).toString() + "-" + 
						startDate.getDate() + " " + 
						startDate.getHours() + ":" + 
						startDate.getMinutes();

				event.allDay = allDay;
				event = { start: startDate, end: endDate, title: "*", id: "-1", allDay: event.allDay };
				$("#%(id_out)s").fullCalendar('renderEvent', event);
				execEventBinded(id.substring(2, id.length), "daysSelect", {"id":event.id, "start":start, "end": end, allDay:event.allDay});
			}			
		});
		$("#%(id_out)s").fullCalendar("changeView", "%(current_view)s");
		$("#%(id_out)s").fullCalendar("gotoDate", new Date(parseInt(%(year)s), parseInt(%(month)s - 1), parseInt(%(day)s)));

	});
</script>"""

	def render(self, parent, contents=""):		
		visibility = "hidden" if self.visible == "0" else "visible"

		view = {"month": "month", "week": "agendaWeek", "day": "agendaDay"}
		id_out = 'o_' + (self.id).replace('-', '_')
		current_view = view[self.view]
		year_month_day = self.displayDate.split('-') if self.displayDate else str(datetime.date.today()).split('-')
		style_params = {"zindex": self.zindex, 
						"top": self.top, 
						"left": self.left, 
						"width": self.width, 
						"height": self.height,
						"visibility": visibility} 
		#raise Exception( style_params )
		mainstyle="""z-index:%(zindex)s; 
					position:absolute; 
					top: %(top)spx; 
					left: %(left)spx; 
					width: %(width)spx; 
					height: %(height)spx;
					visibility: %(visibility)s;"""%style_params
		render_params = {"id_out": id_out,  
					"current_view": current_view, 
					"year": year_month_day[0], 
					"month": year_month_day[1], 
					"day": year_month_day[2],
					"event_sources": self.parse_events(),
					"first_day": self.firstDay}
		return VDOM_fullcalendar.RENDER_SCRIPT%render_params + VDOM_fullcalendar.RENDER_DIV%(id_out, mainstyle)	


	def parse_events(self):
		#eventSources = [{events:[{title: "sldkfj", start:"sldfj", end: "dlsf"}], color: "sldfj"}]
		event_sources = []
		if self.events:
			calendar = vobject.readOne(self.events)
			cal_list = []
			for event in calendar.vevent_list:
				startDate = getattr(event, 'dtstart', vobject.base.ContentLine('DTSTART', '', str(datetime.date.today())))
				endDate = getattr(event, 'dtend', vobject.base.ContentLine('DTEND', '', str(startDate.value)))
				summary = getattr(event, 'summary', vobject.base.ContentLine('SUMMARY', '', 'Event'))
				cal_list.append( [startDate.value, endDate.value, summary.value] )
				description = getattr(event, 'description', vobject.base.ContentLine('DESCRIPTION', '', 'Event'))
				#raise Exception(summary, description)
				#raise Exception(str(endDate).replace('<', '&lt'))
				color = getattr(event, 'color', vobject.base.ContentLine('COLOR','','blue'))
				id = getattr(event, 'id', vobject.base.ContentLine('ID', '', '-1'))
				allDay = getattr( event, 'allday', vobject.base.ContentLine('ALLDAY','','false'))
				allDay = True if allDay.value == "true" else False
				#raise Exception( allDay )
				#allDay = True if str(startDate.value) == str(endDate.value) else False
				if event.rruleset == None:
					event_sources.append({ "events": [{ "id": id.value,
										"title": str(summary.value).replace( "&comma&", ","), 
										"description":description.value,
										"start": startDate.value.strftime("%Y-%m-%d %H:%M:%S"), 
										"end": endDate.value.strftime("%Y-%m-%d %H:%M:%S"),
										"allDay": allDay,
										"instance": "-1"}],
										"color": color.value})
				else:
					instance = 1
					for rev in event.rruleset:
						event_sources.append({ "events": [{
										"title": str(summary.value).replace( "&comma&", ","), 
										"description":description.value,
										"id": id.value,
										"start": rev.strftime("%Y-%m-%d %H:%M:%S"), 
										"end": (rev + relativedelta(endDate.value, startDate.value)).strftime("%Y-%m-%d %H:%M:%S"),
										"allDay": allDay,
										"instance": instance }],
										"color": color.value})
						instance += 1
		return json.dumps(event_sources)


	def wysiwyg(self, parent, contents=""):
		minsize = min(int(self.width), int(self.height))
		result=""\
			"<container id=\"%(id)s\" visible=\"%(visible)s\" zindex=\"%(zindex)s\" hierarchy=\"%(hierarchy)s\" order=\"%(order)s\" top=\"%(top)s\" left=\"%(left)s\" width=\"%(width)s\" height=\"%(height)s\">"\
				"<svg>"\
					"<rect opacity=\"0.5\" x=\"0\" y=\"0\" width=\"%(width)s\" height=\"%(height)s\" fill=\"#FFFFFF\" stroke=\"#CCCCCC\"/>"\
					"<rect opacity=\"0.5\" x=\"0\" y=\"20\" width=\"%(width)s\" height=\"%(height20)s\" fill=\"#EEEEEE\" stroke=\"#CCCCCC\"/>"\
				"</svg>"\
				"<text top=\"5\" left=\"5\" width=\"%(width)s\" color=\"#333fff\">Full Calendar</text>"\
			"</container>"% {
				"id":			self.id,
				"visible":		self.visible,
				"zindex":		self.zindex,
				"hierarchy":	self.hierarchy,
				"order":		self.order,
				"top":			self.top,
				"left":			self.left,
				"width":		str(int(self.width)-20),
				"height":		str(int(self.height)-20),
				"height20":		str(int(self.height) - 20)
				}
		return VDOM_object.wysiwyg(self, parent, contents=result)
