class VDOM_grid(VDOM_object):

	def compute(self):
		pass

	def render(self, parent, contents=""):
		if self.visible=="0":
			display = " diplay: none; "
		else: display = ""
		container_id = "o_" + self.id.replace("-","_")
		container_id_without_o = self.id.replace("-","_")
		import cStringIO
		resultIO = cStringIO.StringIO()
		resultIO.write(u"""
		<style>
		</style>
		""");

		map(resultIO.write, [ u"<div class='tablecontainer'  style='", unicode(display)])
		map(resultIO.write, [ u"overflow: auto; position: absolute; top:", self.top, "px;"])
		map(resultIO.write, [ " left:", self.left, "px; max-width:", self.width, "px; width:", self.width])
		map(resultIO.write, [ "px;max-height:", self.height, "px; z-index:", self.zindex, "; color:#", self.color, ";'>"])
		#resultIO += "<div style ='width:" + str(int(self.width) - 30) + "px;"])
		map(resultIO.write, [ "<form style = 'display:none' method='post' action='' id='", container_id, "_buttonform' name='", container_id, "_buttonform'>"])
		map(resultIO.write, [ "<input type='hidden' id='", container_id, "Button' name = 'Button'/>"])
		map(resultIO.write, [ "<input type = 'hidden'  id='", container_id, "Values' name = 'Values'/>"])
		map(resultIO.write, [ "<input type = 'hidden'  id='", container_id, "RowID' name = 'RowID'/></form>"])
		map(resultIO.write, [ "<form method='post' action='' id='", container_id, "_form' name='", container_id, "_form'>"])
		map(resultIO.write, [ "<table  cellpadding='0' cellspacing='0' class='display' id='", container_id, "' style='width:100%; top: 20px; color:#", self.color, ";'>"])
		resultIO.write( "<thead><tr>")

		columns = self.columns.split(';');
		types = []
		imgs = []
		widths = []
		columnNames = []
		highlightedColumns = []
		dataTableColumns_IO = cStringIO.StringIO();
		dataTableColumns_IO.write("[")
		tableSort = "false"
		for column in columns:
			if len(column) > 0:
				name = "<unknown>"
				type = "label"
				highlightedColumn = 0
				visible = "true"
				sortable = "false"
				searchable = "false"
				sortIndex = "0"
				width = "auto"
				img = ""
				params = column.split(',')
				for param in params:
					if len(param) > 0:
						a = param.split(':');
						if a[0] == "name":
							name = a[1]
						elif a[0] == "type":
							tt = a[1].split('=');
							type = tt[0]
							if len(tt) > 1:
								options = tt[1].split('|')
						elif a[0] == "img":
							img = a[1]
						elif a[0] == "visible":
							visible = a[1]
						elif a[0] == "sortable":
							sortable = a[1]
						elif a[0] == "sortindex":
							sortIndex = a[1]
						elif a[0] == "searchable":
							searchable = a[1]
						elif a[0] == "highlighted":
							highlightedColumn = a[1]
						elif a[0] == "width":
							try:
								width = str(int(a[1])) + "px"
							except Exception, e:
								width = "auto"							
				if len(types) > 0:
					print >>dataTableColumns_IO, ","
				print >>dataTableColumns_IO, '{"bVisible":', visible, ','
				print >>dataTableColumns_IO, '"bSearchable":', searchable, ','
				map(dataTableColumns_IO.write, ['"sWidth": "', width, '",'])
				dataTableColumns_IO.write('"sType": "html",')
				print >>dataTableColumns_IO, '"bSortable":', sortable, '}'
				if sortable == "true":
					tableSort = "true"
				types.append(type)
				imgs.append(img)
				widths.append(width)
				columnNames.append(name)
				highlightedColumns.append(highlightedColumn)
				map(resultIO.write, [ "<th>", name.encode('UTF-8'), "</th>"])

		dataTableColumns_IO.write(']')
		dataTableColumns = dataTableColumns_IO.getvalue()
		dataTableColumns_IO.close()

		resultIO.write("</tr></thead><tbody>")
		num = 0
		if self.data and self.data != None and self.data != "":
			try:
				from xml.dom.minidom import parseString
				#debug("grid.data.len="+str(len(self.data)))
				dom_param = parseString(self.data.encode("UTF-8"))
				rows = dom_param.getElementsByTagName("row")				
				#rows = self.data.split(';')
				if rows:
					#debug("grid.rows.len="+str(len(rows)))
					for row in rows:				
						if row.firstChild:
							idnode = row.getElementsByTagName("id")
							if idnode and idnode[0].firstChild:
								rowid = idnode[0].firstChild.nodeValue.encode('UTF-8')
							else: rowid = "row_id"
							items = row.getElementsByTagName("item")
							map(resultIO.write, [ '\n<tr id="', rowid, '">'])
							rowid = rowid.replace("'", "\\'")
							num = 0
							for itemnode in items:

								if itemnode.firstChild:
									item = itemnode.firstChild.nodeValue.encode('UTF-8').replace('<', '&lt;').replace('>', '&gt;')
								else: item = ""

								if types[num] != "dropdown":
									item = item.replace(" ", "&nbsp;")

								iname = (rowid.decode('UTF-8') + ";" + columnNames[num]).encode('UTF-8')
								#debug("grid:3")
								resultIO.write("<td")
								if highlightedColumns[num] == "yes":
									resultIO.write(" class='B' ")
								resultIO.write(">")	
								#debug("grid:4")
								backgroundIO = cStringIO.StringIO() 

								if itemnode.hasAttribute("style"):
									style = str(itemnode.getAttribute("img"))
								else: style = ""

								if itemnode.hasAttribute("img"):
									img = str(itemnode.getAttribute("img"))
									map (backgroundIO.write, [" style='background-image: url(\"", img, "\");background-repeat: no-repeat;background-position: left center; "])
								elif imgs[num] != "" and types[num] != "button":
									map (backgroundIO.write, [" style='background-image: url(\"", imgs[num], "\");background-repeat: no-repeat;background-position: left center; "])
								else: backgroundIO.write(" style='")

								if itemnode.hasAttribute("text-indent"):
									map (backgroundIO.write, [" text-align:left; text-indent:", str(itemnode.getAttribute("text-indent")), "px; "	])
								if itemnode.hasAttribute("height"):
									map (backgroundIO.write, [" height:", str(itemnode.getAttribute("height")), "px; "])
								if types[num] == "textbutton":
									print >>backgroundIO, " cursor: pointer; "
								if widths[num] != "auto":
									map (backgroundIO.write, [" overflow:hidden; width: ", widths[num], ";"])
								else: backgroundIO.write("overflow:hidden;")
								backgroundIO.write(style)
								backgroundIO.write("'")
								background = backgroundIO.getvalue()
								backgroundIO.close()
								#debug("grid:1")
								if types[num] == "text":										
									map(resultIO.write, [ "<input ", background, " width='100%' type = 'text' name ='", iname, "' value='", item, "'</input></td>"])
								elif types[num] == "radio":				
									map(resultIO.write, [ "<input ", background, "  type = 'radio' name ='radio", rowid, "' value = '", columnNames[num].encode('UTF-8'), "'"])
									if item == "true":
										resultIO.write(" checked")
									resultIO.write("></input></td>")
								elif types[num] == "checkbox":
									map(resultIO.write, [ "<input ", background, " type = 'checkbox' name ='", iname, "'"])
									if item == "true":
										resultIO.write(" checked")
									resultIO.write("></input></td>")
								elif types[num] == "dropdown":
									map(resultIO.write, [ "<select ", background, " name='", iname, "' width='100%'>"])
									if options:
										for option in options:
											oo = option.split('#')
											if len(oo) == 2:
												map(resultIO.write, [ "<option value='", oo[0].encode('UTF-8'), "' "])
												if item == oo[0]:
													resultIO.write(" selected")
												map(resultIO.write, [ ">", oo[1].replace(" ", "&nbsp;").encode('UTF-8'), "</option>"])
									resultIO.write("</select></td>")
								elif types[num] == "button":
									map(resultIO.write, [ "<img style='cursor: pointer; ' src=\"" + imgs[num] +"\" onclick=\""])
									map(resultIO.write, [ """
														var tr = jQuery(jQuery(this).parent('td').parent('tr')); 
														values = '';
														jQuery('input', tr).each(function(t){
														values += jQuery(this).attr('name') + ':' + jQuery(this).attr('value') + ',';});
														jQuery('select', tr).each(function(t){
														values += jQuery(this).attr('name') + ':' + jQuery(this).attr('value') + ',';});
														"""])
									map(resultIO.write, [ " jQuery('#", container_id, "Button').attr('value', '", columnNames[num].encode('UTF-8'), "');"])
									map(resultIO.write, [ " jQuery('#", container_id, "Values').attr('value', values);"])
									map(resultIO.write, [ " jQuery('#", container_id, "RowID').attr('value', '", rowid, "');"])
									map(resultIO.write, [ " jQuery('#", container_id, "_buttonform').submit();"])
									#execEventBinded('", container_id_without_o, "', 'buttonclick',  {RowID: '", rowid, "', Button: '", columnNames[num].encode('UTF-8'), "', Values: values });"
									resultIO.write("\"/></td>")
								elif types[num] == "textbutton":
									map(resultIO.write, [ "<div ", background, ' title="', item.replace('"', '\\"'), '" onclick="'])
									map(resultIO.write, [ """
														var tr = jQuery(jQuery(this).parent('td').parent('tr')); 
														values = '';
														jQuery('input', tr).each(function(t){
														values += jQuery(this).attr('name') + ':' + jQuery(this).attr('value') + ',';});
														jQuery('select', tr).each(function(t){
														values += jQuery(this).attr('name') + ':' + jQuery(this).attr('value') + ',';});"""])
									map(resultIO.write, [ " execEventBinded('", container_id_without_o, "', 'buttonclick',  {RowID: '", rowid, "', Button: '", columnNames[num].encode('UTF-8'), "', Values: values });"])
									if item == "":
										item = "&nbsp;"
									map(resultIO.write, [ "\">", item, "</div>"])
								else: map(resultIO.write, [ "<div ", background, ' title="', item.replace('"', '\\"'), '" >', item, "</div></td>"])
								num += 1
							resultIO.write('</tr>')
			except Exception, e:
				debug("Error in grid render:" + str(e))
				debug("resultIO=" + resultIO.getvalue().decode('UTF-8'))
		map(resultIO.write, [ """</tbody>
			</table>
			<input type='hidden' name='changedids' id='""", container_id, """_changedids' value='' />
			</form>
			</div>
			<script> 
				var idIndex""", container_id, """ = """, str(num), """;
				var selectedIndex""", container_id, """ = """, str(num + 1), """;
				var changedIndex""", container_id, """ = """, str(num + 2), """;"""])
		if self.selectable == "0":
			map(resultIO.write, [ "\n				var singleSelect", container_id, " = false;"])
		elif self.selectable == "2":
			map(resultIO.write, [ "\n				var singleSelect", container_id, " = true;"])

		rows = "0"
		paginate = "false"
		if self.rows != "0":
			try:			
				rows = str(int(self.rows))
				paginate = "true"
			except:
				rows = "0"


		map(resultIO.write, [ """
				var oTable""", container_id, """;
				refresh""", container_id, """();

			function refresh""", container_id, """()
			{
				oTable""", container_id, """ = jQuery('#""", container_id, """').dataTable(
				{
					"bAutoWidth": false,
					"bPaginate": """, paginate,""",
					"iDisplayLength":""", rows, """,
					"bLengthChange": false,
					"bFilter": false,
					"bSort": """, tableSort, """,
					"bInfo": false,
					"aoColumns":""", dataTableColumns, """,
					"fnRowCallback": function( nRow, aData, iDisplayIndex ) {
								jQuery(nRow).unbind();
								"""])
		if self.selectable == "0" or self.selectable == "2":
			map(resultIO.write, [ """	

							jQuery(nRow).click( function() {
								/* Check if we are adding or removing a row */
								if ( aData[selectedIndex""", container_id, """] == 1 )
								{
									aData[selectedIndex""", container_id, """] = 0;																						
									this.className = this.className.replace( /_selected/, '' );
								}
								else
								{
									aData[selectedIndex""", container_id, """] = 1;
									this.className = this.className+'_selected';																						
								}
								var lastElementId = aData[idIndex""", container_id, """];
								var aSelected = new Array();;
								var aTables = oTable""", container_id, """.dataTableSettings;
								for ( var j=0 ; j<aTables.length ; j++ )
								{
									if (aTables[j].sTableId == '""", container_id, """')
									{
										var aaData = aTables[j].aoData;
										for ( var i=0 ; i<aaData.length ; i++ )
										{
											if ( aaData[i]._aData[selectedIndex""", container_id, """] == 1 )
											{"""])
			if self.selectable == "0":
				map(resultIO.write, [ """
												aSelected.push(aaData[i]._aData[idIndex""", container_id, """].toString());"""])
			else:
				map(resultIO.write, [ """	
												if (aaData[i]._aData[idIndex""", container_id, """] != lastElementId)
													{
														aaData[i]._aData[selectedIndex""", container_id, """] = 0;
														var el = document.getElementById(aaData[i]._aData[idIndex""", container_id, """]);
														el.className = el.className.replace( /_selected/, "" );
													}
													else aSelected.push(aaData[i]._aData[idIndex""", container_id, """].toString());"""])

			map(resultIO.write, [ """
											}
										}
									}
								}
								execEventBinded('""", container_id_without_o, """', 'selectionchanged',  {SelectedRows: aSelected.join(',').replace('&', '&amp;')});
							} );"""])
		map(resultIO.write, [ """
							jQuery(nRow).bind("mouseenter", function() {
								if (this.className.indexOf('_hovered') < 0)
									this.className = this.className+'_hovered';
							} );
							jQuery(nRow).bind("mouseleave", function() {
								this.className = this.className.replace('_hovered', '' );
							} );
							aData[selectedIndex""", container_id, """] = 0;
							aData[changedIndex""", container_id, """] = 0;
							jQuery(nRow).find('input').change(function() {
								aData[changedIndex""", container_id, """] = 1;
							} );
							jQuery(nRow).find('select').change(function() {
								aData[changedIndex""", container_id, """] = 1;
							} );
							/* For each draw - set the class based on the stored data */
							nRow.className = (aData[selectedIndex""", container_id, """] == 1) ?
								nRow.className+'_selected' :
								nRow.className.replace( /_selected/, '' );
							nRow.id = aData[idIndex""", container_id, """];
							return nRow;
						}
				});
			}

				function megasubmit""", container_id, """()
				{
/* TODO: dobavit' perebor tablits, kak v funkcii vyshe */
                    var aa = oTable""", container_id, """.fnSettings().aoData;
                    var ret = '';
                    var ch = jQuery('#""", container_id, """_changedids');
                    ch.val('');
                    if (aa.length > 0) {
                        var reta = new Array();
                        for (var i=0; i<aa.length; i++) {
                            if (aa[i]._aData[changedIndex""", container_id, """] == 1) {
                                var xxa = new Array();
                                for (var x=0; x<idIndex""", container_id, """; x++) {
                                    var oo = jQuery(aa[i]._aData[x]);
                                    if (oo.length > 0) {
										var name = jQuery(oo[0]).attr("name");
										if ((name != "") && (name != undefined))
											xxa.push(name);
                                    }
                                }
                                xx = xxa.join(',');
                                reta.push(aa[i]._aData[idIndex""", container_id, """].toString() + ':' + xx);
                                aa[i]._aData[changedIndex""", container_id, """] = 0;
                            }
                        }
                        ret = reta.join(';');
                    }
                    if (ret != '') {
                        ch.val(ret);
                        jQuery('#""", container_id, """_form').submit();
                    }
				}		

				function selectNone""", container_id, """()
				{
					var aTables = oTable""", container_id, """.dataTableSettings;
					for ( var j=0 ; j<aTables.length ; j++ )
					{
						if (aTables[j].sTableId == '""", container_id, """')
						{
							var aaData = aTables[j].aoData;

							for ( var i=0 ; i<aaData.length ; i++ )
							{
								aaData[i]._aData[selectedIndex""", container_id, """] = 0;
								aaData[i].nTr.className = aaData[i].nTr.className.replace( /_selected/, '' );
								//jQuery("#" + aaData[i]._aData[idIndex""", container_id, """]).className = 
								//	jQuery("#" + aaData[i]._aData[idIndex""", container_id, """]).className.replace( /_selected/, '' );
							}
						}
					}
					execEventBinded('""", container_id_without_o, """', 'selectionchanged',  {SelectedRows: ''});
				}		

				function selectAll""", container_id, """()
				{
					var aTables = oTable""", container_id, """.dataTableSettings;
					aSelected = new Array();
					for ( var j=0 ; j<aTables.length ; j++ )
					{
						if (aTables[j].sTableId == '""", container_id, """')
						{
							var aaData = aTables[j].aoData;

							for ( var i=0 ; i<aaData.length ; i++ )
							{
								if (aaData[i]._aData[selectedIndex""", container_id, """] != 1)
								{
									aaData[i]._aData[selectedIndex""", container_id, """] = 1;
									aaData[i].nTr.className += '_selected';
								}
								aSelected.push(aaData[i]._aData[idIndex""", container_id, """].toString());
							}
						}
					}
					execEventBinded('""", container_id_without_o, """', 'selectionchanged',  {SelectedRows: aSelected.join(',').replace('&', '&amp;')});
				}		
				"""])
		resultIO.write("</script>")
		result = resultIO.getvalue().decode('UTF-8')
		resultIO.close()		
		return VDOM_object.render(self, parent, contents=result)

	def wysiwyg(self, parent, contents=""):

		result="<container id=\"%s\" zindex=\"%s\" hierarchy=\"%s\" order=\"%s\" top=\"%s\" left=\"%s\" width=\"%s\" height=\"%s\">"\
					"<svg>"\
						"<rect x=\"%s\" y=\"%s\" width=\"%s\" height=\"%s\" fill=\"#EEEEEE\" fill-opacity=\".4\" />"\
					"</svg>%s"\
				"</container>"%(
					self.id, self.zindex, self.hierarchy, self.order, self.top, self.left, self.width, self.height, 
					0, 0, self.width, self.height, 
					contents)

		return VDOM_object.wysiwyg(self, parent, contents=result)
