import collections
import json


GLOB_JS = u"""<script type="text/javascript">
(function($) {

    function raiseClickEvent(evt, vdomList_v2, obj, is_dbl, is_ctrl){

      var is_child = false;
      var li_obj;

      if (!(obj.get(0).nodeName.toLowerCase() == "li" && obj.attr("itemid"))){
        is_child = true;
        li_obj = obj.closest("li[itemid]");
      } else {
        li_obj = obj;
      }

      var item_id = li_obj.attr("itemid");
      var evt_name = (is_child ? "child": "") + "item" + \
                     (is_ctrl  ? "ctrl" : "") + \
                     (is_dbl   ? "dbl"  : "") + "click";

      if (!is_child){
        var need_select = (vdomList_v2.data("clickAndSelect") == "ctrldblclick" && is_dbl && is_ctrl) || \
                          (vdomList_v2.data("clickAndSelect") == "dblclick" && is_dbl) || \
                          (vdomList_v2.data("clickAndSelect") == "ctrlclick" && is_ctrl) || \
                          (vdomList_v2.data("clickAndSelect") == "click");
        if (need_select){
          if (vdomList_v2.data("multiSelection"))
            vdomList_v2.vdomList_v2("toogleSelection", [item_id]);
          else
            vdomList_v2.vdomList_v2("markAsSelected", [item_id]);
        }
      }

      var params = {
        "itemid":  item_id,
        "selected": vdomList_v2.vdomList_v2("getSelectedIDs"),
        "X": evt.pageX,
        "Y": evt.pageY,
        "child": is_child ? JSON.stringify({class: obj.attr("class")}) : ""
      };

      vdomList_v2.vdomList_v2("markAsActive", item_id);
      execEventBinded(vdomList_v2.data("objID"), evt_name, params);
    }

    function raiseItemSelectedEvent(vdomList_v2){
      execEventBinded(vdomList_v2.data("objID"), "rowsselected", {"selected": vdomList_v2.vdomList_v2("getSelectedIDs")});
    }

    function toogleSelection(item){
      if (item.hasClass('selectable')){
        if (item.hasClass('selected')){
          item.removeClass('selected');
        } else {
          item.addClass('selected');
        }
      }
    }

    var methods = {

        getSelectedIDs: function(){
          ids = [];
          $('>ul>li[itemid].selected', this).each(function(){
            ids.push($(this).attr('itemid'));
          });
          return ids;
        },

        getSelectedItems: function(){
          return $('>ul>li[itemid].selected', this);
        },

        toogleSelection: function(ids, raiseEvent){
          if (typeof ids === "undefined"){
            if (this.data("multiSelection"))
              $('>ul>li[itemid]', this).each(function(){
                toogleSelection($(this));
              });
          } else {
            if (this.data("multiSelection")){
              for (var x in ids) {
                toogleSelection($('>ul>li[itemid="'+ids[x].replace('"', '&quot;')+'"]', this));
              }  
            } else {
              toogleSelection($('>ul>li[itemid="'+ids[0].replace('"', '&quot;')+'"]', this));
            }     
          }
          if (raiseEvent)
            raiseItemSelectedEvent(this);
        },

        markAsSelected: function(ids, raiseEvent){
          if (typeof ids === "undefined"){
            if (this.data("multiSelection")){
              $('>ul>li[itemid].selectable', this).addClass('selected');
            }
          } else {
            if (this.data("multiSelection")){
              for (var x in ids) {
                $('>ul>li[itemid="'+ids[x].replace('"', '&quot;')+'"].selectable', this).addClass('selected');
              }  
            } else {
              var old_sel_item = $('>ul>li[itemid].selectable.selected', this);
              old_sel_item.removeClass('selected');

              var new_sel_item = $('>ul>li[itemid="'+ids[0].replace('"', '&quot;')+'"].selectable', this);
              if (new_sel_item.length)
                new_sel_item.addClass('selected');
              else
                old_sel_item.addClass('selected');
            }       
          }
          if (raiseEvent && $('>ul>li[itemid].selectable', this).length)
            raiseItemSelectedEvent(this);
        },

        unmarkAsSelected: function(ids, raiseEvent){
          if (typeof ids === "undefined"){
            $('>ul>li[itemid].selectable', this).removeClass('selected');
          } else {
            for (var x in ids) {
              $('>ul>li[itemid="'+ids[x].replace('"', '&quot;')+'"].selected', this).removeClass('selected');
            }           
          }
          if (raiseEvent)
            raiseItemSelectedEvent(this);
        },

        markAsActive: function(itemid){
          $('>ul>li[itemid].active', this).removeClass('active');
          $('>ul>li[itemid="'+itemid.replace('"', '&quot;')+'"]', this).addClass('active');
        },

        markAsInactive: function(itemid){
          if (itemid)
            $('>ul>li[itemid="'+itemid.replace('"', '&quot;')+'"]', this).removeClass('active');
          else
            $('>ul>li[itemid].active', this).removeClass('active');
        },

        init: function(options){

            var vdomList_v2 = this;
            var obj_id = vdomList_v2.attr("id");

            vdomList_v2.data("multiSelection", options["multiSelection"]);
            vdomList_v2.data("clickAndSelect",  options["clickAndSelect"]);
            vdomList_v2.data("domID", "#" + obj_id);
            vdomList_v2.data("objID", obj_id.substring(2, obj_id.length));

            if (options["droppable"]){
                $(">ul>li.droppable", this).droppable({
                    addClasses: false,
                    hoverClass: "ui-state-active",
                    tolerance: "pointer",
                    drop: function(ev, ui) {
                        execEventBinded(vdomList_v2.data("objID"), "itemdropped", {itemid: $(this).attr('itemid')});
                    }
                });
            }

            var selectors = [">ul>li"];
            if (options["clickClasses"]){
                var classes = options["clickClasses"];
                for (var i in classes){
                    selectors.push(selectors[0] + " " + classes[i]);
                }
            }

            $(selectors.join(","), this).bind("click dblclick", function(e){
                if (e.preventDefault){
                    e.preventDefault();
                } else {
                    e.returnValue = false;
                }

                var $this = $(this);

                if (e.type == 'dblclick') {
                    $this.data('isSingleClick', false);
                    if (e.ctrlKey || e.metaKey){
                        raiseClickEvent(e, vdomList_v2, $this, true, true);
                    } else {
                        raiseClickEvent(e, vdomList_v2, $this, true, false);
                    }
                } else {
                    setTimeout(function(){
                        if ($this.data('isSingleClick') == true){
                            if (e.ctrlKey || e.metaKey){
                              raiseClickEvent(e, vdomList_v2, $this, false, true);
                            } else {
                              raiseClickEvent(e, vdomList_v2, $this, false, false);
                            }
                        }
                    }, 200);
                    $this.data('isSingleClick', true);
                }
                return false;
            });
        }
    }

    $.fn.vdomList_v2 = function(method){
        if (methods[method]){
            return methods[method].apply(this, Array.prototype.slice.call(arguments, 1));
        } else if (typeof method == 'object' || ! method ) {
            return methods.init.apply(this, arguments);
        } else {
            $.error("VDOM List V2: unknown method " + method);
        }
    };

}(jQuery));
</script>"""


CLICK_N_SELECT = (
  "click",
  "ctrlclick",
  "dblclick",
  "ctrldblclick",
  "manual",
)


class VDOM_listv2(VDOM_object):

    def parse_property(self, pname, ptype, *args, **kwargs):
        prop = getattr(self, pname)
        if not prop:
            return ptype()

        try:
            value = json.loads(prop, *args, **kwargs)

        except Exception as ex:
            if prop:
                raise Exception(u"Can't parse '%s'. It must be '%s'" % (pname, ptype.__name__))

            value = ptype()

        return value

    def render(self, parent, contents=""):

        obj_id = self.id.replace('-', '_')

        items = self.parse_property("data", dict, object_pairs_hook=collections.OrderedDict)
        selected_rows = self.parse_property("selectedrows", list)
        selectable_rows = self.parse_property("selectablerows", list)
        droppable_rows = self.parse_property("droppablerows", list)
        active_row = self.activeitem
        multi_selection = True if self.selectionmode == "1" else False
        already_selected = False

        # compatibility with old version
        can_be_droppable = False
        if self.droppable == "1":
            if not droppable_rows:
                can_be_droppable = True

        can_be_selectable = False
        if not selectable_rows:
            can_be_selectable = True

        result = []
        index = 1
        is_first = True

        for key, value in items.iteritems():

            css_class = u"list-item-{index}{is_first}{is_last}{is_odd}{is_active}{is_selected}{is_selectable}{is_droppable}".format(
                index=index,
                is_first=" list-item-first" if is_first else "",
                is_last=" list-item-last" if index == len(items) else "",
                is_odd=" list-item-odd" if index % 2 == 0 else "",
                is_active=u" active" if active_row == key else u"",
                is_selected=u" selected" if key in selected_rows and not already_selected else u"",
                is_selectable=u" selectable" if key in selectable_rows or can_be_selectable else u"",
                is_droppable=u" droppable" if key in droppable_rows or can_be_droppable else u""
            )

            result.append(u"""<li itemid="{key}" class="{css_class}">{content}</li>""".format(
                key=key,
                css_class=css_class,
                content=value
            ))

            if not multi_selection and key in selected_rows:
              already_selected = True

            index += 1
            is_first = False

        options = {
          "droppable": self.droppable == "1",
          "clickClasses": self.clickclass.strip().split(),
          "multiSelection": multi_selection,
          "clickAndSelect": CLICK_N_SELECT[int(self.selectonclick)],
        }

        output = u"""<div id="o_{id}" name="{name}" objtype="listv2" ver="{version}" style="{display};z-index: {zindex};position: absolute;top: {top}px;"""\
        """left: {left}px;height: {height}px;width: {width}px" class="{css_class}">
    <ul>
        {content}
    </ul>
</div>
<style type="text/css">
#o_{id} ul {{
  list-style: none outside none;
  margin: 0;
  padding: 0;
}}
#o_{id} ul li {{
  display: {item_display};
}}
{css}
</style>
<script type="text/javascript">jQuery(document).ready(function(){{jQuery("#o_{id}").vdomList_v2({options});}});</script>""" 

        contents += GLOB_JS + output.format(
            version=self.type.version,
            name=self.name,
            css_class=self.classname,
            content="".join(result),
            id=obj_id,
            css=self.style % {"id": "o_" + obj_id},
            display=u"display:none;" if self.visible == "0" else u"",
            zindex=self.zindex,
            top=self.top,
            left=self.left,
            height=self.height,
            width=self.width,
            item_display="inline" if self.layout == "1" else "list-item",
            options=json.dumps(options),
        )

        return VDOM_object.render(self, parent, contents=contents)        

    def wysiwyg(self, parent, contents=""):

        import utils.wysiwyg
        image_id = "e1246b00-381e-4b32-bad7-4a647a08d557"
        result = utils.wysiwyg.get_empty_wysiwyg_value(self, image_id)
        return VDOM_object.wysiwyg(self, parent, contents=result)
