from utils.imaging import VDOM_imaging
class VDOM_menu(VDOM_object):


	def subscribe_image(self, title, image, imagename, rollover, rollovername):
		imagefile = None
		rolloverfile = None
		if image:
			resource =application.resources.get_by_label(self.id, imagename)
			if resource:
				imagefile = resource.id
			else:
				im1 = VDOM_imaging()
				im1.create_font(name = self.fontfamily.replace('"', "'"), size = int(self.fontsize))#, fontstyle = self.fontstyle, fontweight = self.fontweight)
				im1.load(application.id,image)
				color1 = "000000"
				if self.color:
					color1 = self.color
				im1.write_text( title, align=self.fontalign, ident=self.fontshift, color=(int(color1[:2],16), int(color1[2:4],16), int(color1[4:6],16)))
				imagefile = im1.save_temporary(application.id,self.id,imagename)
		if rollover:
			resource = application.resources.get_by_label(self.id, rollovername)
			if resource:
				rolloverfile = resource.id
			else:
				im2 = VDOM_imaging()
				im2.create_font(name = self.fontfamily.replace('"', "'"), size = int(self.fontsize))#, fontstyle = self.fontstyle, fontweight = self.fontweight)
				im2.load(application.id,rollover)
				color1 = "000000"
				if self.color:
					color1 = self.color
				im2.write_text( title, align=self.fontalign, ident=self.fontshift, color=(int(color1[:2],16), int(color1[2:4],16), int(color1[4:6],16)))
				rolloverfile = im2.save_temporary(application.id,self.id,rollovername)
		return (imagefile, rolloverfile)

	def compute(self):
		VDOM_object.compute(self)
		if getattr(self,"pages", None):
			return
		if self.currentnode and self.currentnode != "":
			current = application.objects.search(self.currentnode)
		else:
			current = application.objects.search(request.container.id)
		app_map = application.structure
		self.pages = []
		if not app_map:
			return
		if self.level in app_map:
			currentlevel = app_map[self.level]
			if current:
				currentitem = currentlevel.level_map[current.id] if current.id in currentlevel.level_map else None
				currentitem_name = application.objects.search(currentitem.id).name if current.id in currentlevel.level_map else ""
				currentitem_title = application.objects.search(currentitem.id).attributes.title if current.id in currentlevel.level_map else ""
			else:
				currentitem = None
				currentitem_name = ""
				currentitem_title = ""
			self.pages = []
			neighbours_hash = {}
			self.parents_list = []
			self.neighbours_list = []
			self.children_list = []
			shown_current = True
			currentitem_parents = []
			parent_map_children = []
			currentitem_children = []

			if currentitem:
				currentitem_parents.extend(currentitem.parents)
				get_index = lambda obj: getattr(obj, "index")
				currentitem_parents.sort(None, get_index)
				for parent in currentitem_parents:  
					if self.parents == "1":
						obj = application.objects.search(parent.id)
						self.parents_list.append((parent.id,obj.name,obj.attributes.title))
						self.pages.append((parent.id,obj.attributes.title,obj.name))
					parent_map = currentlevel.level_map[parent.id]
					if self.neighbours == "1":
						parent_map_children.extend(parent_map.children)
						parent_map_children.sort(None, get_index)
						for neighbour in parent_map_children:
							if currentitem.id == neighbour.id:
								if self.current == "1" and shown_current:
									obj = application.objects.search(currentitem.id)
									self.pages.append(("",obj.attributes.title,obj.name))
									self.neighbours_list.append(("",obj.name,obj.attributes.title))
									shown_current = False
							elif neighbour not in  neighbours_hash:
								obj = application.objects.search(neighbour.id)
								neighbours_hash[neighbour] = None
								self.neighbours_list.append((neighbour.id,obj.name,obj.attributes.title))
								self.pages.append((neighbour.id,obj.attributes.title,obj.name))	


				if shown_current and self.current == "1":
					obj = application.objects.search(currentitem.id)
					self.pages.append(("",obj.attributes.title,""))
					self.neighbours_list.append(("",obj.name,obj.attributes.title))
				if self.children == "1":
					currentitem_children.extend(currentitem.children)
					currentitem_children.sort(None, get_index)
					for child in currentitem_children:
						obj = application.objects.search(child.id)
						self.children_list.append((child.id,obj.name,obj.attributes.title))
						self.pages.append((child.id,obj.attributes.title,obj.name))
			self.finalheight = self.height
			self.finalwidth = self.width
			self.imagefiles = []
			self.rolloverfiles = []
			self.headerheight = 0
			self.headerwidth = 0
			shown_current = True

			if self.image:
				self.pages = []
				im1 = VDOM_imaging()
				im2 = VDOM_imaging()
				if self.menuheader and self.menuheader != "":
					self.pages.append(("","menuheader",""))
					im1.load(application.id, self.menuheader)
					self.headerheight = str(im1.size[1])
					self.headerwidth = str(im1.size[0])
					self.imagefiles.append(self.menuheader)
					self.rolloverfiles.append("")

				if self.parents == "1":
					for (id,name, title) in self.parents_list:
						self.pages.append((id, title, name))
						(image, rollover) = self.subscribe_image(title, self.image, "image"+name, self.rollover, "rollover"+name)
						self.imagefiles.append(image)
						if self.rollover:
							self.rolloverfiles.append(rollover)

				if self.neighbours == "1":
					for (id,name, title) in self.neighbours_list:
						self.pages.append((id, title, name))
						if id == "":
							if self.current == "1" and shown_current:
								if self.selected:
									__img = self.selected
								elif self.rollover:
									__img = self.rollover
								else:
									__img = self.image
								(image, rollover) = self.subscribe_image(currentitem_title, __img, "selected"+currentitem_name, None,None)
								self.imagefiles.append(image)
								self.rolloverfiles.append("")
								shown_current = False
						else:
							(image, rollover) = self.subscribe_image(title, self.image, "image"+name, self.rollover,"rollover"+name)
							self.imagefiles.append(image)
							if self.rollover:
								self.rolloverfiles.append(rollover)

				if shown_current and self.current == "1":
					self.pages.append((current.id, currentitem_title, currentitem_name))
					if self.selected:
						__img = self.selected
					elif self.rollover:
						__img = self.rollover
					else:
						__img = self.image
					(image, rollover) = self.subscribe_image(currentitem_title, __img, "selected"+currentitem_name, None,None)	
					self.imagefiles.append(image)
					self.rolloverfiles.append("")

				if self.children == "1":
					for (id,name, title) in self.children_list:
						self.pages.append((id, title, name))
						(image, rollover) = self.subscribe_image(title, self.image, "image"+name, self.rollover,"rollover"+name)
						self.imagefiles.append(image)
						if self.rollover:
							self.rolloverfiles.append(rollover)

				im1.load(application.id,self.image)
				self.finalheight = str(im1.size[1])
				self.finalwidth = str(im1.size[0])

				if self.menufooter and self.menufooter != "":
					self.pages.append(("","menufooter",""))
					im1.load(application.id,self.menufooter)
					self.footerheight = str(im1.size[1])
					self.footerwidth = str(im1.size[0])
					self.imagefiles.append(self.menufooter)
					self.rolloverfiles.append("")

	def render(self,parent, contents=""):
		if self.visible == "1":
			vis=""
		else:
			vis="display:none;"
		if len(self.pages) == 0:
			return ""
		import utils
		result = ""
		header = 0
		if not self.layout or self.layout == "" or self.layout == "0":
			separator = "<br>"
		else:
			separator = "&nbsp; &nbsp;"
		if self.image and len(self.pages) != len(self.imagefiles) or (self.rollover and len(self.imagefiles) != len(self.rolloverfiles)):
			return ""
		for i in xrange(0,(len(self.pages))):
			representation = ""
			if self.image:
				if self.rollover and self.rolloverfiles[i] != "":
					onmouse = " OnMouseOver=\"this.src=\'"+utils.id.id2link1(self.rolloverfiles[i])+"\'\" OnMouseOut=\"this.src=\'"+utils.id.id2link1(self.imagefiles[i])+"\'\""
				else:
					onmouse = ""
				representation = "<IMG src=%s %s title=\"%s\" style=\"border:none\" width=\"%s\" height=\"%s\">"%(utils.id.id2link1(self.imagefiles[i]),onmouse,self.pages[i][1],self.finalwidth,self.finalheight)
			else:
				representation = self.pages[i][1]
			if self.pages[i][0] == "":
				link_render_1 = ""
				link_render_2 = ""
			else:
				style = ""
				if self.linkcolor:
					style = 'style="color: #%s"' % self.linkcolor
				link_render_1 = "<a href=\"%s.vdom\" %s>"%(self.pages[i][2],style)
				link_render_2 = "</a>"

			if self.image:
				if self.pages[i][1]=="menuheader":
					top_style = "top: 0px;"
					left_style = "left: 0px;"
					header = 1
				elif self.layout == "0" or self.layout == 0:
					top_style = "top: "+str(int(self.headerheight)+(i-header)*int(self.finalheight))+"px;"
					left_style = "left: 0px;"
				else:
					top_style = "top: 0px;"
					left_style = "left: "+str(int(self.headerwidth)+(i-header)*int(self.finalwidth))+"px;"

				width_style = "width: %spx; "%(self.finalwidth,)
				height_style = "height: %spx; "%(self.finalheight,)
				style = "position: absolute; z-index: "+self.zindex+"; "+left_style+top_style+ width_style + height_style
				result +=  "<div style=\""+style+"\">"+link_render_1+representation+link_render_2+"</div>"
				text_align = ""
			else:
				fontstyle=""
				fontstyles = {"text-align" : "", "font" : "", "color" : "", "text-decoration" : ""}
				if "" != self.color:
					fontstyles["color"] = " #%s;" % self.color
				if "" != self.fontweight and self.fontweight == "bold":
					fontstyles["font"] += self.fontweight + " "
				if "" != self.fontstyle and self.fontstyle == "italic":
					fontstyles["font"] += self.fontstyle + " "
				if "" != self.fontsize:
					fontstyles["font"] += self.fontsize + "px "
				else:
					fontstyles["font"] += "12px "
				if "" != self.fontfamily:
					fontstyles["font"] += self.fontfamily.replace('"', "'")
				fontstyles["font"] += ";"

				for key in fontstyles:
					if "" != fontstyles[key]:
						fontstyle += (key + ":" + fontstyles[key])
				result += "<span style=\"" + fontstyle+"\">" + link_render_1 + representation + link_render_2 + separator + "</span>"
				#text_align = "text-align: center;"
				text_align = "text-align: left;"
		mainstyle="%s z-index: %s;position: %s;top: %spx;left: %spx; width: %spx; height: %spx; %s"%(vis, self.zindex, self.position, self.top, self.left, self.width, self.height, text_align)
		id = 'o_' + (self.id).replace('-', '_')

		return "<div id=\"%s\" style=\"%s\">%s</div>"%(id, mainstyle, result)

	def wysiwyg(self, parent, contents=""):

		if self.image and len(self.pages) != len(self.imagefiles) or (self.rollover and len(self.imagefiles) != len(self.rolloverfiles)):
			return ""

		result = ""
		result += "<container id=\"%s\" zindex=\"%s\" hierarchy=\"%s\" top=\"%s\" left=\"%s\" width=\"%s\" height=\"%s\">"%(
						self.id, self.zindex, self.hierarchy, self.top, self.left, self.width, self.height)

		result +="<svg>"

		if not self.image:
			result +="</svg>"

		if not self.layout or self.layout == "" or self.layout == "0":
			separator = "\n"
		else:
			separator = "  "

		representation = ""
		header = 0

		for i in xrange(0,(len(self.pages))):
			if self.image:
				if self.pages[i][1]=="menuheader":
					top_style = "0"
					left_style = "0"
					header = 1
					width_style = int(self.headerwidth)#self.finalwidth
					height_style = int(self.headerheight)#self.finalheight
				elif self.pages[i][1]=="menufooter":
					top_style = str(int(self.headerheight)+(i-header)*int(self.finalheight))
					left_style = "0"
					width_style = int(self.footerwidth)
					height_style = int(self.footerheight)
				elif self.layout == "0" or self.layout == 0:
					top_style = str(int(self.headerheight)+(i-header)*int(self.finalheight))
					left_style = "0"
					width_style = self.finalwidth
					height_style = self.finalheight
				else:
					top_style = "0"
					left_style = str(int(self.headerwidth)+(i-header)*int(self.finalwidth))
					width_style = self.finalwidth
					height_style = self.finalheight

				representation += "<image y=\"%s\" x=\"%s\" width=\"%s\" height=\"%s\" href=\"#Res(%s)\" />"%(
										top_style, left_style, width_style, height_style, self.imagefiles[i])
			else:
				representation += "%s%s"%(self.pages[i][1], separator)

		if self.image:
			result += representation
			result +="</svg>"
		else:
			if representation:
				result +="<text top=\"%s\" left=\"%s\" width=\"%s\" height=\"%s\" color=\"#%s\" textalign=\"left\" fontfamily=\"%s\" fontsize=\"%s\" fontstyle=\"%s\" fontweight=\"%s\">%s</text>"%(
							0, 0, self.width, self.height, self.color, self.fontfamily.replace('"', '').replace("'", ''), self.fontsize, self.fontstyle, self.fontweight, "<![CD"+"ATA[" + representation + "]"+"]>")

		result += "</container>"

		return VDOM_object.wysiwyg(self, parent, contents=result)
