	#	self.types={self.type.id: self.type}
	#	self.containers={}
	#	self.libraries={}
	def __init__(self, vdomclass, name, action_name, context, index):
		self.__original=vdomclass
		self.name=name
		self.id=vdomclass.id
		self.type=vdomclass.type
		try: self.order=vdomclass.attributes["order"].value
		except: self.order=0
		self.dynamic={(action_name, context): vdomclass.type.dynamic}
		self.attributes=vdomclass.attributes#DummyAttributes()
		self.actions=vdomclass.actions
		self.types={self.type.id: self.type}
		self.containers={}
		self.libraries={}

	def get_attributes(self):
		return self.attributes

	def set_attribute_ex(self, name, value, x = 0):
		old_value = self.attributes[name].value
		self.attributes[name].value = value
		return { "value": value, "old_value": old_value }


	def get_objects_list(self):
		return self.__original.get_objects_list()


class VDOM_objectview(VDOM_object):

	tmp_list = []


	def create_dupes_recurse(self, application, res, object, children):
		result = res
		for index, xobject in enumerate(children):

			# create new object

			#obj_new = application.objects.create(xobject.type.id, object.id)
			d_name = "vdomclass_%d" % index

			obj_new = DummyObject(xobject, d_name, e2vdom.virtual_context, object, 0)
			obj_new_id = obj_new.id

			# add to list of created objects for delete later

			self.tmp_list.append(obj_new_id)

			# copy object name if this is not 1st child container in objectviewtemplate

			#if not obj_new.parent.type.container == 2:
			#if not obj_new.parent.parent:
			#	obj_new.set_name(xobject.name)
			try:
				obj_new.set_name(xobject.name)
			except:
				pass

			# copy actions

			#obj_new.actions = copy.copy(xobject.actions)

			# copy properties

			#for name, val in xobject.get_attributes().iteritems():
			#	obj_new.set_attribute_ex(name, val, False)
			obj_attr = xobject.get_attributes()
			for a in obj_attr:
				obj_new.set_attribute_ex(obj_attr[a].name, obj_attr[a].value, False)

			#result.append({
			#	"object": obj_new
			#})

			if xobject.type.container == 2:
				result = self.create_dupes_recurse(application, result, obj_new, xobject.get_objects_list())

		return result


	def do_compile(self, object):
		result = ""

		if self.vdomclassid:
			self.tmp_list = []

			# get object with id == vdomclassid

			classname = u"ov" if self.classname == "" else self.classname

			source_object = application.objects.search(self.vdomclassid)

			if not source_object:
				result += u"Invalid vdomclass"
				return result

			if 'get_attributes' in dir(source_object):
				source_object_attr = source_object.get_attributes()
			else:
				debug('Object has no attributes: %s' % source_object)
				source_object_attr = DummyAttributes()

			# parse ov.data

			#obj_data_t = u"""%s""" % (self.data).replace("\n", "")
			try:
				obj_data = json.loads(self.data, object_pairs_hook = collections.OrderedDict)
			except:
				obj_data = {}

			nn = 1
			nn_first = 1
			#for key in obj_data:
			for index, key in enumerate(obj_data):

				# create new object for item container

				#obj = application.objects.create('0bb24c16-14fe-08ef-858f-ba24aafc8049', object.id)
				d_name = "vdomclass_%d" % index
				obj = DummyObject(source_object, d_name, e2vdom.virtual_context, source_object, index)
				obj_id = obj.id
				self.tmp_list.append(obj_id)

				#obj_attr = source_object.get_attributes()
				for a in source_object_attr:
					obj.set_attribute_ex(source_object_attr[a].name, source_object_attr[a].value, False)
				obj.set_attribute_ex('data', obj_data[key], False)

				#obj.actions = source_object.actions
				obj.actions = copy.copy(source_object.actions)

				res = self.create_dupes_recurse(application, result, obj, source_object.get_objects_list())

				if nn_first == 1:
					nn_first = 0
					classfirst = u"%s-item-first" % classname
				else:
					classfirst = ''

				if nn == len(obj_data):
					classlast = u"%s-item-last" % classname
				else:
					classlast = ''

				if nn % 2:
					classodd = ''
				else:
					classodd = u"%s-item-odd" % classname

				item_data_id = key
				item_data = obj_data[key]

				obj.set_attribute_ex("visible", "1", False)
				obj.set_attribute_ex("width", source_object_attr['width'].value, False)
				obj.set_attribute_ex("height", source_object_attr['height'].value, False)
				obj.set_attribute_ex("data", item_data, False)
				obj.set_attribute_ex("dataid", item_data_id, False)
				obj.set_attribute_ex("classauto", "%(class)s-item %(class)s-item-%(nn)s %(classfirst)s %(classlast)s %(classodd)s" % {
					 "class":      classname
					,"nn":         str(nn)
					,"classfirst": classfirst
					,"classlast":  classlast
					,"classodd":   classodd
				}, False)
				obj.set_attribute_ex("classname", "%(userdef)s" % {
					 "name":        obj.name
					,"userdef":    source_object_attr["classname"].value
				}, False)
				result += managers.engine.render(application, obj, object, "vdom")

				nn += 1

		#debug("!!!!!!!! result ")
		#debug(result)

		return result


	def __init__(self, id):
		VDOM_object.__init__(self, id)

	def render(self, parent, contents = ""):

		# render items

		inner_render = self.do_compile(application.objects.search(self.id))

		# remove on_compile's objects

		#managers.source_cache.invalidate(application.id, self.id)
		#for object_id in self.tmp_list:
		#	application.objects.delete(object_id)

		# render object

		display = u" display:none; " if self.visible == "0" else u""

		e2vdom.process(self, parent)

		if self.overflow == "1":
			overflow = u"hidden"
		elif self.overflow == "2":
			overflow = u"scroll"
		elif self.overflow == "3":
			overflow = u"visible"
		else:
			overflow = u"auto"

		height = u"height: %spx;" % self.height

		style = u"""{display} z-index: {zind}; position: {pos}; top: {top}px; left: {left}px;
					width: {width}px; overflow: {overflow}; {height}"""\
				.format(display = display, zind = self.zindex, pos = self.position,
						top = self.top, left = self.left, width = self.width, overflow = overflow, height = height)

		woid = (self.id).replace('-', '_')
		id = u"o_" + woid

		classname = u"""class="%s" """ % self.classname if self.classname else u""

		classname_item = (self.classname).replace(' ', '')
		if self.classname == '':
			classname_item = 'ov'

		# layout css for inner items
		if self.layout == "0":
			layout = u"float: left;"
		elif self.layout == "1":
			layout = u"clear: both;"
		else:
			layout = u"width: 100% !important; clear: both;"

		style_items = u"""
		<style type="text/css">
		#%(id)s .%(class)s-item {
		%(layout)s
		}
		</style>""" % {
			"id":      id
			,"class":  classname_item
			,"layout": layout
		}

		js = u"""<script type="text/javascript">
		%(id)s_doo = false;
		$j(document).ready(function(){
			//$j("#%(id)s>.%(class)s-item").live('click dblclick', function(e){
			//$j("#%(id)s>.%(class)s-item").live('click', function(e){
			$j("#%(id)s>.%(class)s-item").bind('click dblclick', function(e){
				if (e.preventDefault) e.preventDefault(); else e.returnValue = false;
				var t = $j(this);
				if (e.type == 'dblclick') {
					%(id)s_doo = false;
					execEventBinded('%(woid)s', "itemdblclick", {"id":t.attr("dataid")});
				} else {
					setTimeout(function() {
						if (%(id)s_doo == true) {
							execEventBinded('%(woid)s', "itemclick", {"id":t.attr("dataid")});
						}
					}, 300);
					%(id)s_doo = true;
				}
				return false;
			});
			$j("#%(id)s>.%(class)s-item").hover(function(e){
				execEventBinded('%(woid)s', "itemmouseover", {"id":$j(this).attr("dataid")});
				return false;
			},function(e){
				execEventBinded('%(woid)s', "itemmouseout", {"id":$j(this).attr("dataid")});
				return false;
			});
			$j("#%(id)s>.%(class)s-item").draggable({
				connectToDynatree: true,
				appendTo: 'body',
				containment: 'document',
				zIndex: 99999,
				opacity: 0.6,
				helper: 'clone',
				option: 'accept',
				cursor: 'default',
				cursorAt: { left: -2, top: 4 },
				//cursorAt: { left: 20, bottom: -2 },
				//cursorAt: { left: -5, top: -5 },
				start: function(e,u){
					execEventBinded('%(woid)s', "dragstart", {itemid: $(this).attr('dataid')});
				}
				,helper: function(e) {
					return $("<div class='ui-widget-header'>drop item to...</div>");
				}
			});
		});
		</script>""" % {
			"id":      id
			,"woid":   woid
			,"class":  classname_item
		}

		result = u"""<div objtype='objectview' ver='2011-10-12' objname='{name}' id="{id}" style="{style}" {classname}>{style_items}{contents}</div>{js}""".format(
			id = id, name = self.name, js = js,
			style = style.strip(), style_items = style_items.strip(), 
			classname = classname.strip(), contents = inner_render
		)


		return VDOM_object.render(self, parent, contents=result)


	def wysiwyg(self, parent, contents=""):

		# remove on_compile's objects

		#managers.source_cache.invalidate(application.id, self.id)
		#for object_id in self.tmp_list:
		#	application.objects.delete(object_id)

		html_items = u""
		datalen = 0
		if self.data != "":
			#obj_data_t = u"""%s""" % (self.data).replace("\n", "")
			try:
				obj_data = json.loads(self.data, object_pairs_hook=collections.OrderedDict)
			except:
				obj_data = {}
			datalen = len(obj_data)
			nn = 0
			for key in obj_data:
				nn += 1
				item_data_id = key
				item_data = obj_data[key]
				b, tail = item_data[:0], item_data
				while len(tail)>2:
					b += tail[:2] + "&shy;"
					tail = tail[2:]
				data = b + tail
				html_items += u"<div><b>{nn}</b> <i>{id}</i><br />{data}</div>".format( nn = nn, id = item_data_id, data = data )

		if self.layout == "0":
			layout = u"width: 150px;"
		elif self.layout == "1":
			layout = u"width: 150px; clear: both;"
		else:
			layout = u"width: 100%; clear: both;"

		html = u"""
		<style type="text/css">
		.ov_container {
		postition: relative;
		margin: 4px 4px 0 4px;
		}
			.ov_container div {
			background: #ddd;
			border: 1px solid #ccc;
			color: #777;
			float: left;
			font-size: 10px;
			height: 55px;
			overflow: hidden;
			margin: 0 1px 1px 0;
			padding: 1px;
			white-space: wrap;
			%(layout)s
			}
			.ov_container div b {
			font-color: #333;
			}
			.ov_container div i {
			color: #222;
			font-style: normal;
			font-size: 12px;
			}
		</style>
		<div class="ov_container">%(items)s</div>
		""" % { "items": html_items, "layout": layout }

		html = "<![CDATA[" + html + "]]" + ">"

		result = u"""<container id="{id}" visible="{vis}" zindex="{zind}" hierarchy="{hierarchy}" order="{order}"
							top="{top}" left="{left}" width="{width}" height="{height}">
						<svg>
							<rect x="0" y="0" width="{width}" height="{height}" fill="#EEEEEE"/>
						</svg>{contents}
						<text textalign="right" fontweight="bold" top="{ttop}" left="{tleft}" color="#aaaaaa">Object View ({len})</text>
						<htmltext top="2" left="2" width="{hwidth}" height="{hheight}">{html}</htmltext>
					</container>
				""".format(id = self.id, vis = self.visible, zind = self.zindex, hierarchy = self.hierarchy, order = self.order,
						top = self.top, left = self.left, width = self.width, height = self.height,
						contents = "" #contents
						,tleft   = int(self.width) - 130
						,ttop    = int(self.height) - 24
						,hwidth  = int(self.width) - 8
						,hheight = int(self.height) - 24
						,html    = html
						,len     = datalen
					)

		return VDOM_object.wysiwyg(self, parent, contents=result)
