	def create_dupes_recurse(self, application, res, object, children):
		result = res
		for index, xobject in enumerate(children):

			# create new object

			#obj_new = application.objects.create(xobject.type.id, object.id)
			d_name = "vdomclass_%d" % index

			obj_new = DummyObject(xobject, d_name, e2vdom.virtual_context, object, 0)
			obj_new_id = obj_new.id

			# add to list of created objects for delete later

			self.tmp_list.append(obj_new_id)

			# copy object name if this is not 1st child container in objectviewtemplate

			#if not obj_new.parent.type.container == 2:
			#if not obj_new.parent.parent:
			#	obj_new.set_name(xobject.name)
			try:
				obj_new.set_name(xobject.name)
			except:
				pass

			# copy actions

			#obj_new.actions = copy.copy(xobject.actions)

			# copy properties

			#for name, val in xobject.get_attributes().iteritems():
			#	obj_new.set_attribute_ex(name, val, False)
			obj_attr = xobject.get_attributes()
			for a in obj_attr:
				obj_new.set_attribute_ex(obj_attr[a].name, obj_attr[a].value, False)

			#result.append({
			#	"object": obj_new
			#})

			if xobject.type.container == 2:
				result = self.create_dupes_recurse(application, result, obj_new, xobject.get_objects_list())

		return result


	def do_compile(self, object, items, onlydiv):
		result = ""

		if self.vdomclassid:
			self.tmp_list = []

			#get object with id == vdomclassid

			classname = u"ov" if self.classname == "" else self.classname

			source_object = application.objects.search(self.vdomclassid)

			if not source_object:
				result += u"Invalid vdomclass"
				return result

			vdomclass_w = source_object.attributes['width'].value
			vdomclass_h = source_object.attributes['height'].value

			if 'get_attributes' in dir(source_object):
				source_object_attr = source_object.get_attributes()
			else:
				debug('Object has no attributes: %s' % source_object)
				source_object_attr = DummyAttributes()

			# parse ov.data

			try:
				obj_data = json.loads(self.data, object_pairs_hook = collections.OrderedDict)
			except:
				obj_data = {}

			if isinstance(obj_data, list):
				obj_data = collections.OrderedDict([ (key, {} ) for key in obj_data ])

			nn = 1
			nn_first = 1
			styleforitem = ''

			for index, key in enumerate(obj_data):

				# create new object for item container

				d_name = "vdomclass_%d" % index
				obj = DummyObject(source_object, d_name, e2vdom.virtual_context, source_object, index)
				obj_id = obj.id
				self.tmp_list.append(obj_id)

				for a in source_object_attr:
					obj.set_attribute_ex(source_object_attr[a].name, source_object_attr[a].value, False)
				obj.set_attribute_ex('data', obj_data[key], False)

				obj.actions = copy.copy(source_object.actions)

				res = self.create_dupes_recurse(application, result, obj, source_object.get_objects_list())

				if nn_first == 1:
					classfirst = u"%s-item-first" % classname
				else:
					classfirst = ''

				if nn == len(obj_data):
					classlast = u"%s-item-last" % classname
				else:
					classlast = ''

				if nn % 2:
					classodd = ''
				else:
					classodd = u"%s-item-odd" % classname

				item_data_id = key
				item_data = obj_data[key]

				classes = u"%(class)s-item %(class)s-item-%(nn)s %(classfirst)s %(classlast)s %(classodd)s %(notloaded)s" % {
					 "class":      classname
					,"nn":         str(nn)
					,"classfirst": classfirst
					,"classlast":  classlast
					,"classodd":   classodd
					,"notloaded":  "not-loaded" if onlydiv else ""
					}

				obj.set_attribute_ex("visible", "1", False)
				obj.set_attribute_ex("width", source_object_attr['width'].value, False)
				obj.set_attribute_ex("height", source_object_attr['height'].value, False)
				obj.set_attribute_ex("data", item_data, False)
				obj.set_attribute_ex("dataid", item_data_id, False)
				obj.set_attribute_ex("classauto", classes, False)
				obj.set_attribute_ex("classname", "%(userdef)s" % {
					 "name":       obj.name
					,"userdef":    source_object_attr["classname"].value
				}, False)

				#if self.dynamicloading == "1" and onlydiv:
				if onlydiv:
					# render for requested items
					try:
						###tmp_res = managers.engine.render(application, obj, object, "vdom")
						#tmp_res = managers.engine.render(application.itself, obj, object, "vdom")
						#result += re.sub('^.*(<div.*dataid=[^>]*>).*(<\/div>)$', '\\1\\2', tmp_res, flags=re.M|re.I|re.S)

						#if nn_first == 1:
						#	tmp_res = managers.engine.render(application.itself, obj, object, "vdom")
						#	re.purge()
						#	#z = re.sub("^.*?<div[^>]*?style=[\"\'](.*?)[\'\"]", "\\1", tmp_res, flags=re.M|re.I|re.S)
						#	z = re.match("^.*?<div[^>]*?style=[\"\']([^\'^\"]*?)[\'\"]", tmp_res)
						#	if z.group(1):
						#		styleforitem = z.group(1)
						styleforitem = u"position:relative;width:%spx;height:%spx;display:block;" % ( vdomclass_w, vdomclass_h )

						result += u'<div class="%(classes)s" dataid="%(id)s" style="%(style)s"></div>' % {
							"id": item_data_id,
							"classes": classes,
							"style": styleforitem
							}
					except:
						raise
				else:
					# first render - output all items, but as empty DIVs
					#result += managers.engine.render(application, obj, object, "vdom")
					result += managers.engine.render(application.itself, obj, object, "vdom")

				nn += 1
				nn_first = 0

		return result


	def __init__(self, id):
