	def render(self, parent, contents = ""):

		woid = (self.id).replace('-', '_')
		id = u"o_" + woid

		#itemslimit = int("0%s" % (self.itemslimit).strip())
		#if itemslimit <= 0: itemslimit = 10

		itemstorender = {}
		onlydiv = False

		try:
			itemstorender = json.loads(self.data, object_pairs_hook = collections.OrderedDict)
		except:
			itemstorender = {}

		if isinstance(itemstorender, list):
			onlydiv = True
			if itemstorender == []:
				itemstorender.append("dummy-item-554f180b-1c2a-6705-3608-87f71366710b")

		inner_render = self.do_compile(application.objects.search(self.id), itemstorender, onlydiv)
		print "inner_render %%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%", inner_render

		if request.render_type == "e2vdom":
			# render called from action

			tmp_id = u"ov" + str(utils.uuid.uuid4())

			if self.dynamicloading == "0":  # in order to save compatibility with older version

					js = u"""
var x = $j('#%(id)s>div.ov-content');
$j('>div[dataid]', x).remove();
var ovtmp = $j('#%(tmp_id)s:last');
if ($j.trim(ovtmp.html()) != '') {
	$j(">span.ov-eoi", x).before( $j('>div[dataid]', ovtmp) );
}
						""" % {
							"id":     id,
							"tmp_id": tmp_id,
							"woid":   woid
						}

			else:

				if onlydiv:   # [] - empty frames: rerender
					js = u"""
var x = $j('#%(id)s>div.ov-content');
$j('>div[dataid]', x).remove();
var ovtmp = $j('#%(tmp_id)s:last');
if ($j.trim(ovtmp.html()) != '') {
	$j(">span.ov-eoi", x).before( $j('>div[dataid]', ovtmp) );
	/*$j('>div[dataid]',ovtmp).each(function(){
		$j(">span.ov-eoi", x).before($j(this));
	});*/
	vdom_ov_markShow("%(woid)s");
}
						""" % {
							"id":     id,
							"tmp_id": tmp_id,
							"woid":   woid
						}
				else:         # {} - items with content: render items in empty frames
					js = u"""
var ovtmp = $j('#%(tmp_id)s:last');
if ($j.trim(ovtmp.html()) != '') {
	var x = $j('#%(id)s>div.ov-content');
	$j('>div[dataid]',ovtmp).each(function(){
		var i = $j(">div[dataid="+$j(this).attr('dataid')+"]", x);
		if (i.size() > 0) {
			i.before($j(this));
			i.remove();
			//} else {
			//	$j(">span.ov-eoi", x).before($j(this));
		}
	});
	vdom_ov_markShow("%(woid)s");
}
						""" % {
							"id":     id,
							"tmp_id": tmp_id,
							"woid":   woid
						}

			result = u"""
<noreplace/>
<div id="%(tmp_id)s" style="display:none">%(inner_render)s</div>
<script type='text/javascript'>
 	%(js)s
</script>
			""" % {
				"js":           js,
				"inner_render": inner_render,
				"id":           id,
				"tmp_id":       tmp_id,
				"woid":         woid
			}

			return VDOM_object.render(self, parent, contents = result)

		else:
			# initial render objectview

			display = u" display:none; " if self.visible == "0" else u""

			e2vdom.process(self, parent)

			if self.overflow == "1":
				overflow = u"hidden"
			elif self.overflow == "2":
				overflow = u"scroll"
			elif self.overflow == "3":
				overflow = u"visible"
			else:
				overflow = u"auto"

			height = u"height: %spx;" % self.height

			style = u"""{display} z-index: {zind}; position: {pos}; top: {top}px; left: {left}px;
						width: {width}px; overflow: {overflow}; {height}"""\
				.format(
					display = display, zind = self.zindex, pos = self.position,
					top = self.top, left = self.left, width = self.width,
					overflow = overflow, height = height)

			classname = u"""class="%s" """ % self.classname if self.classname else u""

			classname_item = 'ov' if self.classname == '' else (self.classname).replace(' ', '')

			# items layout

			if self.layout == "1":
				layout = u"float: left;"
			elif self.layout == "0":
				layout = u"clear: both;"
			else:
				layout = u"width: 100% !important; clear: both;"

			style_items = u"""
			<style type="text/css">
			#%(id)s .%(class)s-item {
			%(layout)s
			}
			</style>""" % {
				"id":      id
				,"class":  classname_item
				,"layout": layout
			}

			if self.loading == "0":
				js_loading_call = u""
				js_loading = u""
			else:
				js_loading_call = u"%s_ld(t);" % id
				js_loading = self.JS_LOADING % { "id": id }

			js_dynamic = self.JS_DYNAMIC % { "id": id, "woid": woid, "cssc": classname_item }
			js_dynamic_e2 = self.JS_DYNAMIC_E2 % { "id": id, "woid": woid, "cssc": classname_item }

			js_drag = self.JS_DRAG % { "id": id, "woid": woid, "class": classname_item } if self.draggable == "1" else ""

			js_sortable = self.JS_SORTABLE % { "id": id, "woid": woid } if self.sortable == "1" else ""

			js = self.JS % {
				"js_drag":            js_drag
				,"id":                id
				,"woid":              woid
				,"class":             classname_item
				,"js_loading":        js_loading
				,"js_loading_call":   js_loading_call
				,"js_dynamic":        js_dynamic
				,"js_sortable":       js_sortable
			}

			if VDOM_CONFIG_1["DEBUG"] == "1":
				debug_info = u"objtype='objectview' objname='%s' ver='%s'" % (self.name, self.type.version)
			else:
				debug_info = u""

			result = u"""
				<div {debug_info} id="{id}" style="{style}" {classname}>
					{style_items}
					<div class='ov-content'>
						{contents}
						<span class='ov-eoi' style='display:block;clear:both'></span>
					</div>
				</div>
				{js}
				""".format(
				js = js_dynamic_e2 if request.render_type == "e2vdom" else js,
						id = id,
						debug_info = debug_info,
						style = style.strip(),
						style_items = style_items.strip(),
						classname = classname.strip(),
						contents = inner_render
			)

			return VDOM_object.render(self, parent, contents=result)


