class VDOM_richtext(VDOM_object):

	def render(self, parent, contents=""):
		if self.visible == "1":
			invisible = ""
		else:
			invisible = "display:none;"

		if self.classname == "":
			classname = ""
		else:
			classname = " class='%s' " % ( self.classname )

		style="z-index: %s;position: %s;top: %spx;left: %spx;width: %spx;%s"%(self.zindex, self.position, self.top, self.left, self.width, invisible)
		styles = {"text-align" : "", "font" : "", "color" : "", "text-decoration" : "", "height" : "", "overflow" : ""}
		if "" != self.align:
			styles["text-align"] = " %s;" % self.align
		if "" != self.color:
			styles["color"] = " #%s;" % self.color
		if "" != self.font:
			styles["font"] = " %s;" % self.font
		if "1" == self.underlined:
			styles["text-decoration"] += " underline"
		if "1" == self.strikethrough:
			styles["text-decoration"] += " line-through"
		if "" != styles["text-decoration"]:
			styles["text-decoration"] += ";"
		if "" != self.height and "0" != self.height:
			styles["height"] = " %spx; " % self.height
			styles["overflow"] = " auto; "

		for key in styles:
			if "" != styles[key]:
				style += (key + ":" + styles[key])

		the_value = self.value
		#if "" == the_value:
		#  the_value = "Text"

		id = 'o_' + (self.id).replace('-', '_')

		result="<div id=\"%s\" style=\"%s\" %s>%s</div>"%(id, style, classname, the_value)

		return VDOM_object.render(self, parent, contents=result)

	def wysiwyg(self, parent, contents=""):
		the_value = self.value
		if "" == the_value:
			the_value = '<font color="#444444">[empty]</font>'
		f = ""
		c = ""
		if "" != self.color:
			c = "color=\"%s\"" % self.color
		result="<container id=\"%s\" visible=\"%s\" zindex=\"%s\" hierarchy=\"%s\" order=\"%s\" top=\"%s\" left=\"%s\" width=\"%s\">"\
			"<htmltext top=\"%s\" left=\"%s\" width=\"%s\" %s %s editable=\"%s\">%s</htmltext>"\
			"%s</container>"%(
				self.id, self.visible, self.zindex, self.hierarchy, self.order, self.top, self.left, self.width,
				0, 0, self.width, f, c, "value", "<![CDATA[" + the_value + "]" + "]>",
				contents)
		return VDOM_object.wysiwyg(self, parent, contents=result)
