import src.util
	
class VDOM_search(VDOM_object):

	def compute(self):
		pass				
		
	def render(self, parent, contents=""):
		display = ""
		if self.visible == "0":
			display = " display: none; "
		input_image = ""
		search_button = ""
		id = 'o_' + (self.id).replace('-', '_')
		if self.buttonimage:
			if self.buttonrollover:
				onmouse = " OnMouseOver=\"jQuery(this).addClass('btno')\" OnMouseOut=\"jQuery(this).removeClass('btno')\" "
			else:
				onmouse = ""
			width_style = ""
			height_style = ""
			top_style = ""
			left_style = ""
			if self.buttontop:
				top_style = " top:"+self.buttontop+"px; "
			if self.buttonleft:
				left_style = " left:"+self.buttonleft+"px; "
			if self.buttonwidth:
				width_style = " width:"+self.buttonwidth+"px; "
			if self.buttonheight:
				height_style = " height:"+self.buttonheight+"px; "

			search_button = """<input value='' src='""" + src.util.id.id2link1('1c378464-5553-3eb4-8e12-ebcec2bbee33') + """' type='image' class='btn' style="cursor: pointer; position: absolute;
			""" + top_style + left_style + width_style + height_style + '"' + onmouse + """/>"""
		if self.inputimage:
			width_style = ""
			height_style = ""
			top_style = ""
			left_style = ""
			if self.inputimagetop:
				top_style = " top:"+self.inputimagetop+"px; "
			if self.inputimageleft:
				left_style = " left:"+self.inputimageleft+"px; "
			if self.inputimagewidth:
				width_style = " width:"+self.inputimagewidth+"px; "
			if self.inputimageheight:
				height_style = " height:"+self.inputimageheight+"px; "
				
			input_image = """
			<div style=" background-position:center; background-image:url('""" + src.util.id.id2link1(self.inputimage) + """'); 
			background-repeat:no-repeat; position: absolute;""" + top_style + left_style + width_style + height_style + """ ">
			</div>
			"""

		spanstyle = """<style type=\"text/css\">
		#""" + id + """ input.btn{background-image:url(""" + src.util.id.id2link1(self.buttonimage) + """)}
		#""" + id + """ input.btno{background-image:url(""" + src.util.id.id2link1(self.buttonrollover) + """)}
		span.letters{"""
		styles = {"text-align" : "", "font" : "", "color" : ""}
		if "" != self.color:
			styles["color"] = " #" + self.color + ";"
		if "" != self.fontweight and self.fontweight == "bold":
			styles["font"] += self.fontweight + " "
		if "" != self.fontstyle and self.fontstyle == "italic":
			styles["font"] += self.fontstyle + " "
		if "" != self.textdecoration:
			styles["text-decoration"] = " %s;" % self.textdecoration
		if "" != self.fontsize:
			styles["font"] += self.fontsize + "px "
		else:
			styles["font"] += "12px "
		if "" != self.fontfamily:
			styles["font"] += self.fontfamily
		styles["font"] += ";"	
			
		for key in styles:
			if "" != styles[key]:
				spanstyle += (key + ":" + styles[key])
		spanstyle += "}</style>"
			
		width_style = ""
		height_style = ""
		top_style = ""
		left_style = ""
		if self.inputtop:
			top_style = " top:"+self.inputtop+"px; "
		if self.inputleft:
			left_style = " left:"+self.inputleft+"px; "
		if self.inputwidth:
			width_style = " width:"+self.inputwidth+"px; "
		if self.inputheight:
			height_style = " height:"+self.inputheight+"px; "		
		input_style="position: absolute; border-width:0px; font-size: 10px; padding:0px;" + top_style + left_style + width_style + height_style

		width_style = ""
		height_style = ""
		top_style = ""
		left_style = ""
		if self.letterstop:
			top_style = " top:"+self.letterstop+"px; "
		if self.lettersleft:
			left_style = " left:"+self.lettersleft+"px; "
		if self.letterswidth:
			width_style = " width:"+self.letterswidth+"px; "
		if self.lettersheight:
			height_style = " height:"+self.lettersheight+"px; "		
		letters_style="position: absolute; cursor:pointer;" + top_style + left_style + width_style + height_style
		
		width_style = ""
		height_style = ""
		if self.width:
			width_style = "width: "+self.width +"px; "
		if self.height:
			height_style = "height: "+self.height +"px; "
		style = display + "overflow: hidden; position: absolute; z-index: "+self.zindex+"; left: "+self.left+"px; top: "+self.top+"px; "+ width_style + height_style + "; border: " + self.border + "px solid black;"
		
		if self.containerlink:
			# action = "action=\"/" + self.containerlink + ".vdom\""	# obsolete - page_guid.vdom
			action = "action=\"/" + request.application().search_object(self.containerlink).name + ".vdom\""
		else: action = ""
			
		result = spanstyle + """
			<div id='""" + id + """' style='""" + style +"""'>
			<form """ + action + """ method="get" id='form""" + id + """' ><input name='""" + self.argument + """'
						id='hidden""" + id + """' type="hidden" value=""></form>
			<form>
			""" + input_image + search_button + """			
			<input id='input""" + id + """' style='""" + input_style +"""'  name = '""" + self.argument + """'
				type="text" onfocus="jQuery(this).attr('value','')" value="type your request here"/>
			</form>
			<div style='""" + letters_style +"""'>
		"""
		for letter in self.buttons:
			result +="""
			<span class="letters" onclick="
					document.getElementById('hidden""" + id + """').value='^""" + letter + """';
					document.getElementById('form""" + id + """').submit();
					"
					onmouseover="jQuery(this).css('color','#666666')"
					onmouseout="jQuery(this).css('color','#""" + self.color + """')"
					>""" + letter + """</span>"""
		result += "</div></div>"
		return  VDOM_object.render(self, parent, contents=result)
		
		
	def wysiwyg(self, parent, contents=""):
		input_image = ""
		search_button = ""
		if self.buttonimage:
			width_style = ""
			height_style = ""
			top_style = ""
			left_style = ""
			if self.buttontop:
				top_style = " y=\""+self.buttontop+"\""
			if self.buttonleft:
				left_style = " x=\""+self.buttonleft+"\" "
			if self.buttonwidth:
				width_style = " width=\""+self.buttonwidth+"\" "
			if self.buttonheight:
				height_style = " height=\""+self.buttonheight+"\" "
				
			search_button = "<image href=\"#Res(" + self.buttonimage + ")\" "  + top_style + left_style + width_style + height_style + "/>"

		if self.inputimage:
			width_style = ""
			height_style = ""
			top_style = ""
			left_style = ""
			if self.inputimagetop:
				top_style = " y=\""+self.inputimagetop+"\" "
			if self.inputimageleft:
				left_style = " x=\""+self.inputimageleft+"\" "
			if self.inputimagewidth:
				width_style = " width=\""+self.inputimagewidth+"\" "
			if self.inputimageheight:
				height_style = " height=\""+self.inputimageheight+"\" "
				
			input_image = "<image href=\"#Res(" + self.inputimage + ")\" "  + top_style + left_style + width_style + height_style + "/>"

		
		spanstyle = ""
		if "" != self.color:
			spanstyle += " color=\"#" + self.color + "\""
		if "" != self.fontweight and self.fontweight == "bold":
			spanstyle += " fontweight=\"bold\""
		if "" != self.fontstyle and self.fontstyle == "italic":
			spanstyle += " fontstyle=\"italic\""
		if "" != self.textdecoration:
			spanstyle += " textdecoration=\"" + self.textdecoration + "\""
		if "" != self.fontfamily:
			spanstyle += " fontfamily=\"" + self.fontfamily + "\""			
		if "" != self.fontsize:
			spanstyle += " width=\"" + self.fontsize + "\" "
			spanstyle += " fontsize=\"" + self.fontsize + "\" "
			lsize = int(self.fontsize)
		else:
			spanstyle += " width=\"12\" "
			spanstyle += " fontsize=\"12\" "
			lsize = 12
			
		width_style = ""
		height_style = ""
		top_style = ""
		left_style = ""
		if self.inputtop:
			top_style = " y=\""+self.inputtop+"\" "
		if self.inputleft:
			left_style = " x=\""+self.inputleft+"\" "
		if self.inputwidth:
			width_style = " width=\""+self.inputwidth+"\" "
		if self.inputheight:
			height_style = " height=\""+self.inputheight+"\" "		
		input_style= top_style + left_style + width_style + height_style

		width_style = ""
		height_style = ""
		if self.width:
			width_style = "width=\""+self.width +"\" "
		if self.height:
			height_style = "height=\""+self.height +"\" "
		style = " left=\""+self.left+"\" top=\""+self.top+"\" "+ width_style + height_style
				
		result = """
			<container id=\"""" + self.id + "\" zindex=\"" + self.zindex + """\" 
			hierarchy=\"""" + self.hierarchy + "\" order=\"" + str(self.order) + "\"" + style + """>
			<svg>
			<rect x=\"0\" y=\"0\" """ + width_style + height_style + """ fill=\"#EEEEEE\" fill-opacity=\".4\" />
			""" + input_image + search_button + """<rect""" + input_style +""" fill=\"#FFFFFF\"/>
			</svg>
		"""
		
		width = 50
		height = 50
		top = 0
		left = 0
		if self.letterstop:
			top = int(self.letterstop)
		if self.lettersleft:
			left = int(self.lettersleft)
		if self.letterswidth:
			width = int(self.letterswidth)
		if self.lettersheight:
			height = int(self.lettersheight)
		
		ltop = top
		lleft = left
		for letter in self.buttons:			
			result += "<text top=\"" + str(ltop) + "\" left=\"" + str(lleft) + "\" " + spanstyle +"><![CDATA[" + letter + "]" + "]></text>\n"
			lleft += lsize
			if lleft > width:
				ltop += lsize
				lleft = left
		result += contents + "</container>"	
		#debug("qwertyqwerty")
		#debug(result)
		return VDOM_object.wysiwyg(self, parent, contents=result)	
		
def set_attr(app_id, object_id, param):
	if "buttonimage" in param:
		width, height = get_image_size(param["buttonimage"]["value"], app_id, object_id)
		# set attributes
		import src.xml
		o = src.xml.xml_manager.search_object(app_id, object_id)
		if o.attributes.buttonwidth != width or o.attributes.buttonheight != height:
			o.set_attributes({"buttonwidth": width, "buttonheight": height})		
	if "inputimage" in param:
		width, height = get_image_size(param["inputimage"]["value"], app_id, object_id)
		# set attributes
		import src.xml
		o = src.xml.xml_manager.search_object(app_id, object_id)
		if o.attributes.inputimagewidth != width or o.attributes.inputimageheight != height:
			o.set_attributes({"inputimagewidth": width, "inputimageheight": height})				
	return ""		
	
def get_image_size(res_id, app_id, object_id):
	import src.resource
	ro = src.resource.resource_manager.get_resource(app_id, res_id)
	if not ro:
		return (None,None)
	# get image resource, obtain width and height and set width and height of the object
	from PIL import Image
	import cStringIO
	s = cStringIO.StringIO()
	s.write(ro.get_data())
	s.seek(0, 0)
	im = Image.open(s)
	return im.size
