import json
import utils.wysiwyg

class VDOM_shortcut( VDOM_object ):
	def render(self, parent, contents=""):
		# prepare id	
		id = self.id.replace('-', '_')
		oid = 'o_' + id

		# prepare data
		value = self.data.strip()

		if value and value[0] not in [ '"', '[', '{' ]:
			value = '"%s"' % value

		value = json.loads( value )
		data = {}

		if type(value) in [ str, unicode ]:
			data[ value ] = ''

		if isinstance( value, list ):
			for key in value:
				data[ key ] = ''

		if isinstance( value, dict ):
			for key in value:
				data[ key ] = value[ key ]



		# prepare javascript code
		js = """new VdomShortcut( "%s", %s ).start()""" % ( id, json.dumps(data) )
		js = """<script language='javascript' type='text/javascript'>%s</script>""" % ( js, )


		# prepare result html
		result = """<div id="%s" style="display:none">%s</div>""" % ( oid, js )
		return VDOM_object.render(self, parent, contents=result)


	def wysiwyg(self, parent, contents=""):

		self.width = "42"
		self.height = "42"
		self.zindex = "0"

		image_id = "e49c929a-a509-60ef-e77a-7b4a87061c33"
		result = utils.wysiwyg.get_empty_wysiwyg_value(self, image_id)
		return VDOM_object.wysiwyg(self, parent, contents=result)
